-- ----------------------------
-- Table structure for disease
-- ----------------------------
DROP TABLE IF EXISTS `disease`;
CREATE TABLE `disease` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `plant_id` int(11) NOT NULL COMMENT '植物id',
  `disease_name` varchar(511) NOT NULL COMMENT '病虫害名称',
  `disease_part` varchar(511) DEFAULT NULL COMMENT '发病部位',
  `disease_period` varchar(511) DEFAULT NULL COMMENT '发病时期',
  `disease_type` int(11) NOT NULL COMMENT '0->病害 1->虫害',
  `disease_option_rel` varchar(255) DEFAULT NULL COMMENT '病虫害推理选项、疾病对应关系，不同id以#隔开',
  `disease_description` text COMMENT '概述',
  `disease_symptom` text COMMENT '症状',
  `disease_character` text COMMENT '形态特征',
  `disease_custom` text COMMENT '生活习性',
  `disease_law` text COMMENT '规律',
  `disease_prevention` text COMMENT '防治技术',
  `disease_image` text COMMENT '图片，多个图片路径以#隔开',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '更新时间',
  `delete_time` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=23 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of disease
-- ----------------------------
BEGIN;
INSERT INTO `disease` VALUES (1, 48, '草莓白粉病', '叶', NULL, 0, NULL, '&#12288;&#12288;草莓白粉病是草莓生产中的主要病害，特别是保护地草白粉病，发生严重时，病叶率在45%以上，病果率50%以上，严重影响草莓生产。', '&#12288;&#12288;主要危害叶、果实，而在果梗、叶柄和葡匐茎上很少发生。叶片发病初期在叶背面长出薄薄的白色菌丝，后期菌丝密集成粉状层，病原菌逐渐漫延扩展，严重时叶片正面也滋生菌丝。随病情加重，叶缘逐渐向上卷起呈汤匙状，叶片上发生大小不等的暗色污斑，后期呈红褐色病斑，叶缘开始萎缩，最终整个叶片焦枯死亡。花和花蕾受侵害后，花萼萎蔫，授粉不良，幼果被菌丝包裹，不能正常膨大而干枯。果实后期受害时，果面裹有一层白粉，着色缓慢，果实失去光泽并硬化，严重时整个果实如同一个白粉球，完全不能食用。', NULL, NULL, '&#12288;&#12288;病原为羽衣草单囊壳Sphaerothecaaphanis(Wallr.)Braun。在寒冷地区，病菌以闭囊壳、菌丝体等随病残体留在地上或在活着的草莓老叶上越冬，在温暖地区，多以菌丝或分生孢子在寄主上越冬或越夏。带病草莓苗等繁殖材料是中远距离传播的主要方式。在田间，病原可通过气流传播。<br>发病适温为20°C左右，相对湿度为80%~100%。湿度大利其流行，尤其当高温干旱与高温高湿交替出现，又有大量白粉菌菌源时，易大流行。从11月到翌年5月该病都有发生。大棚里温度高，湿度大，光照差，空气不流通，白粉病较露地草莓发病早而严重；基肥不足，偏施氮肥，植株徒长，枝叶过密，长势弱者均有利于白粉病发生。', '&#12288;&#12288;1.农业防治。选用抗病品种。清理棚内或田间的上茬草莓植株和各种杂草后再定植。不要过量施氮肥，栽植密度不要过大，果农之间尽量不要互相“串棚”，避免人为传播。发现病枝，病果要尽早在晨露未消时轻轻摘下装进方便袋烧掉或深埋<br>&#12288;&#12288;2.药剂防治。发病初期可喷撒唑菌•啶酰菌悬浮剂800〜1 600倍液，或4%四氟醚唑1 000倍液，或50%醚菌酯水分散粒剂每亩4 000倍液，每7天喷1次，连续2〜3次。常用的药剂还有苯醚甲环唑、三唑酮、腈菌唑、氟硅唑、武夷菌素等。药剂应轮换使用。保护地栽培的，也可采用硫磺熏蒸技术，在温室内每百平方米安装一台熏蒸器，熏蒸器内盛20克硫磺粉，在傍晚盖苫后开始加热熏蒸，隔日1次，每次4小时。但熏蒸器温度不可超过280°C，以免亚硫酸对草莓产生药害。温室内夜间温度超过20°C时要酌减药量。500g/L苯菌酮悬浮剂1 500〜3 000倍液,100亿cfu/g枯草芽孢杆菌稀释300〜600倍液，0.5%蛇床子素可溶液剂450〜750倍液，25%粉唑醇悬浮剂1 000〜1 500倍液，42.5%吡唑醚菌酯•氟唑菌酰胺悬浮剂150〜225g.a.i/hm²1 500〜2 000倍液', '/imgupload/disease/caomeibaifenbing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (2, 48, '草莓灰霉病', '叶', NULL, 0, NULL, '&#12288;&#12288;草莓灰霉病是草莓保护地生产中的重要病害，发生普遍，直接危害花器和果实，常造成很大的经济损失。', '&#12288;&#12288;主要为害花器和果实，也为害叶片、叶柄、花柄等。花器发病时花萼呈水渍状针眼大的小斑点，后扩展成近圆形或不规则形病斑，逐渐延伸到子房及幼果，最后幼果形成水渍状病斑，潮湿时迅速软腐，产生灰褐霉层。果实发病多在青果上，开始是果顶呈水渍状病斑，后发展成褐色病斑，湿度大时病果湿软腐烂。空气干燥时病果呈干腐状，成果实提早脱落。叶片发病始于叶基，呈水渍状小斑点，后向外扩展成圆形、半圆形、近圆形或不规则灰褐色大病斑，最后蔓延到全叶，叶片腐烂、枯死，病部常产生灰褐色霉状物，发病后期易引起早期落叶。', NULL, NULL, '&#12288;&#12288;病原为灰葡萄孢Botrytis cinerea Per.。病原菌主要以分生孢子，菌丝体或菌核在病残体和土壤中越冬，在田间可通过气流和露水进行传播。<br>&#12288;&#12288; 温暖湿润的环境有利于发病。大棚连作田块病残物多，草莓发病早且重。偏施氨肥，草莓生长旺盛，叶面大而嫩绿易患灰霉病。过度密植，栽培垄过低，植株基部老叶多，以及垄土积水，棚内通气不良都会导引起发病严重。', '&#12288;&#12288;1.农业防治。选用优良抗病品种，如红福、硕丰等。水旱轮作，或与十字花科蔬菜、豆类轮作。生长期及时摘除病叶、病果、老叶，带出棚外深埋。连作大棚可利用夏季高温消毒土壤。采用高垄地膜 覆盖栽培。收获后彻底清理病残体。<br>&#12288;&#12288;2.药剂防治。在草莓匍匐茎分株繁苗期及时拔除弱苗、病苗，并用药剂预防2 ~ 3次；定植后要重点对发病中心株及周围植株进行防治。药剂可选用50%啶酰菌胺水分散粒剂1 000倍液，或50%克菌丹可湿性粉剂200~300倍液，或25%啶菌•唑乳油每亩1 000倍液，或50%异菌脲可湿粉剂800倍液，或50%腐霉利可湿性粉剂1 000倍液，或40%甲基硫菌灵可湿性粉剂800倍液，每7 ~ 10天喷1次，连续2 ~ 3次。注意轮换交替用药。38%啶酰菌•吡唑醚菌酯水分散粒剂228~342 g.a.i/hm2稀释1 000 ~ 1 500倍液，42.5%吡唑醚菌酯•氟唑菌酰胺悬浮剂150~225 g.a.i/hm2 1 500 ~ 2 000倍液，2 000亿活芽孢/克枯草芽孢杆菌可湿性粉剂1 500 ~ 2 250倍液，50%啶酰菌胺水分散粒剂1 000 ~ 1 500倍液，20% β-羽扇豆球蛋白多肽可溶液剂250 ~ 400倍液。', '/imgupload/disease/caomeihuimeibing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (3, 48, '草莓根腐病', '根', NULL, 0, NULL, '&#12288;&#12288;草莓根腐病是草莓根部重要病害，特别是在多年种植草莓的重茬地块，严重时可造成整个草莓园区的毁灭。', '&#12288;&#12288;主要危害根部，常见有急性型和慢性型两种。急性型多在春夏两季发生，雨后叶尖突然萎凋，不久呈青枯状，引起全株迅速枯死。慢性型定植后至冬初均可发生，从下部叶开始 叶缘变成红褐色，逐渐向上凋萎，以至枯死。根的中心柱呈红色。定植后在新生的不定根上症状最明显，发病初期不定根的中间部位表皮坏死，形成1〜5mm长红褐色之黑褐色梭形长斑，病部不凹陷，病健交界明显，严重时病根木质部及髓坏死褐变，整条根干枯，地上部叶片变黄或萎蔫，最后全株枯死。', NULL, NULL, '&#12288;&#12288;病原为多种病原物和环境相互作用引起。常见的病原菌\r\nRhizoctonia solani K u hn、草莓疫莓Phytophthora fragariae Hickman、褐座坚壳菌Rosellinia necatrix（Hart.）Berlese、菜豆壳球孢菌Macrophomina phaseolina(Maubl.)Ashby、蜜环菌Armillaria mellea (Vahl.ex Fr.)Quel、多种镰刀菌等。病原以卵孢子在地表病残体或土壤中越夏。卵孢子在土壤中可存活多年，条件适应时即萌发形成孢子囊，释放出游动孢子，侵入植物的根系或幼根。\r\n<br>&#12288;&#12288;长期连坐，土壤板结，地力下降，植株根部过度积水或土壤过干，均易发病。其中土壤病原物增加和植株生长势衰弱是主要病因。一般黏土地块比沙壤土易发病。\r\n&#12288;&#12288;病原为多种病原物和环境相互作用引起。常见的病原菌\r\nRhizoctonia solani K u hn、草莓疫莓Phytophthora fragariae Hickman、褐座坚壳菌Rosellinia necatrix（Hart.）Berlese、菜豆壳球孢菌Macrophomina phaseolina(Maubl.)Ashby、蜜环菌Armillaria mellea (Vahl.ex Fr.)Quel、多种镰刀菌等。病原以卵孢子在地表病残体或土壤中越夏。卵孢子在土壤中可存活多年，条件适应时即萌发形成孢子囊，释放出游动孢子，侵入植物的根系或幼根。\r\n<br>&#12288;&#12288;长期连坐，土壤板结，地力下降，植株根部过度积水或土壤过干，均易发病。其中土壤病原物增加和植株生长势衰弱是主要病因。一般黏土地块比沙壤土易发病。', '&#12288;&#12288;1.农业防治。选择早熟避病或抗（耐）病品种，如宝交早生、丰香、因都卡、新明星等。重病田实行4年上的轮作。在草莓采收后，将地里的草莓植株全部挖除，施人大量有机肥，深翻土壤，灌足水，在光照最充分，气温较高的夏季7〜8月，地面用透明塑料薄膜覆盖15天以上，利用太阳能使地温上升到 50 〜 60°C，可消毒土壤。适施氮肥，增施磷钾肥，以充分腐熟的有机肥为主，施足基肥；开花后至果实成熟期间，保证充足的水分供应。严禁大水漫灌，避免灌后积水，有条件可进行滴灌或渗灌。\r\n<br>&#12288;&#12288;2.药剂防治。防治关键要从苗期抓起。在草莓匍匐莲分株繁苗期及时拔除弱苗、病苗。并用药预防 2 〜 3次；定植后要重点对发病中心株及周围植株进行防治；发病时采用灌根或喷洒根茎的方法防治。常用药剂有58%甲霜•锰锌可湿性粉剂，可湿性粉剂500倍液，64%噁霜•锰锌可湿性粉剂500倍液，50%异菌脲可湿性粉剂1 500倍液，72%锰锌•霜脲可湿性粉剂800倍液，霜霉威水剂600〜800倍液。每7〜10天1次，连续2 〜 3次。', '/imgupload/disease/caomeigenfubing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (4, 48, '草莓枯萎病', '叶', NULL, 0, NULL, '&#12288;&#12288;枯萎病是草莓的主要病害，一旦发病就会迅速蔓延，很难根治。', '&#12288;&#12288;症状主要表现在开花至收获期。以病菌侵害根部，先在地上部分表现出病态，心叶变黄绿色或黄绿色卷曲，变小变狭，呈船形，叶片无光泽，下部叶片变紫色枯萎，叶缘褐色向内卷。叶柄和果柄的维管束也变褐色与黑褐色，地下根系呈黑褐色，不长新根，潮湿时近地面基部长出紫红色的分生抱子。', NULL, NULL, '&#12288;&#12288;病原为尖镰孢菌草莓转化型Fusarium oxysporium f.sp.fragariae Winks et Willams。病原体以菌丝体和后垣孢子随病残体遗落土中，或在种子上越冬，在田间可通过病根、病叶通过土壤和水传播。<br>&#12288;&#12288;15 〜 18°C开始发病，22°C以上发病最严重，25°〜30°C会造成冰柱酷似，焉萎也最多。连作，土质黏重，地势低洼，耕作粗放，土壤过酸，施肥不足，偏施氮肥，施用未腐熟肥料等，均能引起植株根系发育不良，都会使病害加重。', '&#12288;&#12288;1.检疫。建立无病繁育区或田，再选择无病株苗作母苗进行繁育。\r\n<br>&#12288;&#12288;2.农业防治。有条件的地区可实施水旱轮作。种植时发现根发黑的病苗要挑出来集中烧毁，病穴施用生石灰进行消毒。\r\n<br>&#12288;&#12288;3.药剂防治。发病初期喷50%多菌灵可湿性粉剂600 〜 700倍液，或70% 代森锰锌干悬粉500倍液，50%苯菌灵可湿性粉剂1 500倍液喷淋茎基部。每隔15天左右防治1次，共防治5 〜 6次', '/imgupload/disease/caomeikuweibing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (5, 48, '草莓褐斑病', '叶', NULL, 0, NULL, '&#12288;&#12288;草莓褐斑病又称轮纹病，是草莓生产中常见病害，严重时可造成幼苗枯死，直接影响草莓生产。', '&#12288;&#12288;主要危害叶片。初生褐紫色小圆斑，扩展后形成大小不等的圆形至椭圆形病斑，病斑中部褪为黄褐色至灰白色，边缘紫褐色，斑面轮纹明显或不明显，其上密生小黑点。', NULL, NULL, '&#12288;&#12288;病原为暗拟茎点霉Dendrophoma obscurans(Ell.et Ev.) Annderson。病原菌以菌丝体和分生孢子器在病叶组织内或随病残体遗落土中越冬，在田间可通过雨水溅射传播。<br>&#12288;&#12288;遇高温干旱，病情受抑，但如遇温暖多湿，特别是时晴时雨的天气频繁出现，病情又扩展，品种间抗性有差异。', '&#12288;&#12288;1.农业防治。因地制宜选用抗病良种，移植前清除种苗病叶及重病株。加强栽培管理，平衡施肥。合理密植，及时摘除老叶病叶，集中销毁。<br>&#12288;&#12288;2.药剂防治。移植时用70%甲基硫菌灵可湿性粉剂500倍液浸苗15〜20分钟。待药液晾干后栽植，发芽至开花前用等量式波尔多液200倍喷洒叶面，每15〜20天1次。发病初期喷撒10%苯醚甲环唑水分散颗粒1 500倍液，或70%甲基硫菌灵可湿性粉剂800倍液，或50%福•甲硫可湿性粉1500倍液， 或40%多•硫悬浮剂或50%硫黄•甲硫灵悬浮剂500倍液，每7〜10天1次，连续2〜3次。', '/imgupload/disease/caomeihebanbing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (6, 48, '草莓蛇眼病', '叶', NULL, 0, NULL, '&#12288;&#12288;草莓轮斑病又称大斑叶点霉褐斑病，是草莓的主要病害。', '&#12288;&#12288;主要为害叶片，早期症状为小叶上现紫红色的圆形小斑点，随着病情发展，多有较明显的轮纹。小斑点沿着主脉扩大成“V”字形或圆形侵蚀斑，直到叶缘。病斑边缘紫红色，中央黄至灰褐色，多有较明显的轮纹，其上密生黄褐至黑褐色小斑点。', NULL, NULL, '&#12288;&#12288;病原为大斑叶点霉Phyllosticta  grandimaculans Bub.et Krieg.。病原菌以菌丝体或分生孢子器在病株或遗落土中的病残体上越冬，在田间可通过气流、雨水、农事操作传播。<br>&#12288;&#12288;病害发生率和严重程度与叶龄有很大关系，新叶时期极易受侵染，其次是叶片湿度大时也很易受侵染，特别是整株淹没灌溉和潮湿多雨期，这种时期越长，染病率越高。温暖多湿天气或栽植过密，田间湿度大发病重。', '&#12288;&#12288;参见草莓褐斑病。', '/imgupload/disease/caomeisheyanbing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (7, 48, '草莓叶枯病', '叶', NULL, 0, NULL, '&#12288;&#12288;叶枯病是草莓的普通病害，各地均有发生。', '&#12288;&#12288;叶片发病，初生紫褐色小斑，一般是在叶缘，后扩展成黄绿色大斑。嫩叶发病常始于叶尖，沿主脉向叶内发展，病斑褐色，边缘深褐色，最后形成“V”字形的大斑，病斑上可出现轮纹，后期病部生出黑褐色小粒点，一般每片叶上只1个大斑。', NULL, NULL, '&#12288;&#12288;病原为草莓日晷壳菌Gnomonia fructicola(Arnaud)Fall 病原菌随病残体在土壤中越冬，在田间可通过气流传播。<br>&#12288;&#12288;品种间抗病性差异明显。温暖潮湿，有利于发病。棚室条件下，棚室内外温差大，棚内温度高光照条件差，叶组织柔嫩易发病。露地草莓遇春季潮湿多雨或大水漫灌也易流行。', '&#12288;&#12288;1.农业防治。选用抗病、耐病品种。施足底肥，增施磷，钾肥，避免大水漫灌。加强棚室温度和湿度及光照管理，适时适量通风换气，防止湿气滞留，减少棚膜和叶面结露。收获后及时清除病老枯叶。<br>&#12288;&#12288;2.药剂防治。发现零星病叶开始喷药，常用药剂有50%腐霉利可湿性粉剂1 500〜2 000倍液，或50%异菌脲可湿性粉剂1 000〜1 500倍液，每7〜10天1次，连续2〜3次。', '/imgupload/disease/caomeiyekubing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (8, 48, '草莓芽枯病', '叶', NULL, 0, NULL, '&#12288;&#12288;草莓牙枯病俗称“烂心病”，是温室和供棚栽培草存的主要病害之一。', '&#12288;&#12288;主要危害蕾、新芽、托叶和叶柄基部，发病初期，花苞和新芽呈现蔫青枯状，后失去活力变成黑褐色而枯死。叶柄基部托叶如被侵染，就是展开的成叶也萎蔫，受害株的叶数，坐果数也减少。重病株则全株死亡。在被害枯死部位往往有第2次寄生的灰真菌产生，成为以后灰霉病发生的原因，轻病株随以后气温的升高，还可以长出新蕾和新芽。', NULL, NULL, '&#12288;&#12288;病原为丝核菌Rhizoctonia solani K u hn 病原菌以菌丝体或菌核随病残体在土壤中越冬，通过雨水、流水、农具以及堆肥传播。<br>&#12288;&#12288;气温低及遇有连阴雨天气，易发病，寒流侵袭或湿度过高发病重。冬春保护地栽培时，开始放风时病情扩展迅速，温室或大棚密闭时间长，发病早且重。', '&#12288;&#12288;1.农业防治。选用抗病品种。施用充分腐熟的堆肥，不要在病田育苗和采苗。适宜的栽植密度和栽植深度，及时摘除病叶和下部枯黄老叶，使通风透光良好，大棚和拱棚栽培时要通风换气。合理流溉，浇水宜安排在上午，浇后迅速放风降湿，防止湿气滞留。<br>&#12288;&#12288;2.药剂防治。从现蕾期开始喷淋，常用药剂有百菌清粉剂600倍液，甲基硫菌灵可湿性粉剂500倍液，10%噁霉灵水剂300倍液，每7天1次，连续2〜3次。', '/imgupload/disease/caomeiyakubing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (9, 48, '草莓角斑病', '叶', NULL, 0, NULL, '&#12288;&#12288;草莓角斑病又称草莓褐角斑病、灰斑病，是草莓苗期的主要病害之一，在南方草莓高产区均有发生。', '&#12288;&#12288;主要危害叶片，发病初期出现暗紫褐色多角形病斑，扩大后变为灰褐色，边缘色深，直径约5mm，后期病斑上有时具轮纹。', NULL, NULL, '&#12288;&#12288;病原为草莓生叶点霉phyllosticta fragaricola Desm.et Rob.。病原菌以分生孢子器在病残体上越冬，在田间可通过用水和灌溉水传播。5 〜 6月为盛发期。', '&#12288;&#12288;参见草莓褐斑病。', '/imgupload/disease/caomeijiaobanbing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (10, 48, '草莓炭疽病', '叶', NULL, 0, NULL, NULL, '&#12288;&#12288;地上部位均可发病，病株受害大体可分为局部病斑和全株萎蔫两类症状。局部病斑在匍匐茎上最易发生，叶片、叶柄和浆果上也常见。茎叶上病斑长3〜 7mm.初为红褐色，后变黑色，溃疡状稍凹陷，病斑包围匍匐茎或叶柄整圈时，病斑以上部位枯死。萎蔫型病株起初病叶边缘发生棕红色病斑，后变褐色或黑色，发病较轻时，叶片白天萎蔫，傍晚时能恢复，发病严重时几天后即枯死。掰断茎部的症状是由外向内逐渐变成褐色或黑色，拔起植株，细根新鲜，主根基部与茎交界处部分发黑。', NULL, NULL, '&#12288;&#12288;病原为草花发疽菌Colletotrichum fragariae Brooks。 病原菌主要以土壤中的病茎叶、匍匐茎等病残体越冬，在田间可通过雨水飞溅传播。品种间抗病性差异明显。病原菌生长的最适温度在28 〜 32°C。', '&#12288;&#12288;农业防治。选择无病田作为苗床。加强草莓苗床健康母株的选择。避免大水泼浇、漫灌。采用加盖遮阳篷、滴灌、沟灌等方法，既可以适当给草莓植株补充水分，又可以降低地温，从而促进草莓生长，减轻病害。<br>&#12288;&#12288;2.药剂防治。草莓匍匐茎伸长是防治的关键时期，田间摘老叶及降雨的前后进行重点防治。药剂选用25%咪鲜胺乳油1000倍液，或75% 百菌清可湿性粉剂600倍液，或20%噻菌铜悬乳剂400倍液，或77% 氢氧化铜可湿性粉剂700倍液，或2%嘧啶核苷类抗生素水剂200倍液，每7 〜 10 天1次，送续2 〜 3次。400g/L 氟硅唑乳油稀释5 000 〜 10 000倍液，2 000亿活芽孢/g枯草芽孢杆菌可湿性粉剂1 500 〜 2 250倍液。', '/imgupload/disease/caomeitanjubing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (11, 48, '草莓革腐病', '果', NULL, 0, NULL, '&#12288;&#12288;革腐病是草莓的重要病害，各地均有分布，发生普遍。', '&#12288;&#12288;主要危害果实。从开花期至成熟期均可发病幼果发病时病部变为黑褐色，后干枯，硬化，如皮革。成熟果发病，病部白腐软化，似开水烫伤。', NULL, NULL, '&#12288;&#12288;病原为恶疫霉Phryophthora cacorum (Leb.e Cohm) Schrr.病原菌以卵孢子在土壤中越冬，在田间年通过雨水或灌溉水传播。地势低注，土壤黏重，偏施氮肥发病重。', '&#12288;&#12288;1. 农业防治。清沟沥水，合理施肥，不偏施氮肥，草莓园内可用谷壳铺设于畦沟内，避免雨水直接落到土壤上。<br>&#12288;&#12288;2 药剂防治。从花期开始喷药，常用药剂有40% 三乙膦酸铝可湿性粉剂150 〜200倍液，或50% 甲霜铜可湿性粉剂600倍液，或60%琥铜•乙膦铝可湿性粉剂500倍液，70%乙铝•锰锌可湿性粉剂500，或58%甲霜•锰锌可湿性粉剂800倍液， 或64%噁霜•锰锌可湿性粉剂500倍液，或72%霜脲氰•锰锌可湿性粉剂800倍液，或70%甲霜•福美双可湿性粉剂800倍液，或70%甲霜。或69%烯酰•锰锌可湿性粉剂或水分散颗粒剂1000倍液，每10天左右1次，连续3 〜 4次。', '/imgupload/disease/caomeigefubing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (12, 48, '草莓病毒病', '叶', NULL, 0, NULL, '&#12288;&#12288;病毒病是草莓生产的重要病害，轻病株一般减产20% 〜 25%，重病毒株可减产40%以上。', '&#12288;&#12288;因病原不同，症状较为复杂，多表现为花叶、黄边、皱叶和斑驳，病株矮化，生长不良，结果减少，品质变劣，甚至不结果。我国草莓主栽区有4种病毒。<br>&#12288;&#12288;1.草存斑驳病毒。单独侵染栽培种草莓时往往不表现明显症状，但病株长势衰退，果实品质下降，与其他病毒复合侵染时可产生斑驳症状。<br>&#12288;&#12288;2.草莓轻型黄边病毒。单独侵染栽培种草莓无明显症状，仅使病株轻微矮化，使草莓长势变弱，产量和果实质量严重下降。<br>&#12288;&#12288;3.草莓镶脉病毒。单独侵染栽培种草莓时，无明显症状，但引起植株生长衰弱，匍匐茎抽生量减少，产量和品质下降，与斑驳病毒，轻型黄边病毒复合侵染后引起病株叶片皱缩、扭曲，植株极度矮化。<br>&#12288;&#12288;4.草莓皱缩病毒。对草莓危害性最大的病毒病，感病植株表现为叶片畸形，叶上产生褪绿斑，沿叶脉出现小的，不规则的褪绿斑及坏死斑，叶脉褪绿、透明，幼叶生长不对称，扭曲皱缩，小叶黄化，叶柄缩短，叶小，植株矮化，病毒株草莓匍匐茎抽生量减少。繁殖力下降，果实变小，草莓产量大幅度下降，甚至绝产。', NULL, NULL, '&#12288;&#12288;草莓病毒的种类多达62种，其中，草莓斑驳病毒Strawberrymottle virus(SmoV)、草莓轻型黄边病毒Strawberry mild yellowedge virus (SMTEV )、草莓镶脉病毒Strawberry vein band virus (SVBV) 和草莓皱缩病毒Strawberry crinkle virus (SCrV) 是侵染我国草莓的4种主要病毒。病毒主要在草莓种株上越冬，在田间可过蚜虫传毒。<br>&#12288;&#12288;品种间抗性有差异。连年种植，病毒病发病率高。', '&#12288;&#12288;1.农业防治。对栽种面积大，品种杂的草莓种植区域应加强检查，一旦发现有连片病毒病发生田块，应及时铲除井销毁。及时清除田边、沟边杂草，做好清洁田园工作，清除寄主植物。种植无病毒苗，2 〜 3年更新1次: 发病率高的地区1年更新1次。统一作物布局，连片种植。无病毒苗的栽植周围2km内不能有老草莓园，不与其他感染病毒的茄科作物间作。茎尖组织培养和花粉培养是草莓无病毒苗培育的一项最为有效的技术。<br>&#12288;&#12288;2.药剂防治。发病初期开始喷药，常用药剂有1.5% 植病灵乳剂1000倍液，0.5% 菇类蛋白多糖水剂300倍液。20% 吗胍•乙酸铜可湿性粉剂500倍液。每隔10 〜 15天防治1次，共防治2 〜 3次。另外，从苗期就要开始治防蚜虫。', '/imgupload/disease/caomeibingdubing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (13, 48, '草莓青枯病', '叶', NULL, 0, NULL, NULL, '&#12288;&#12288;症状主要危害茎部，多在定植初期发生。发病初期，下位叶1 〜 2片凋萎，叶柄下垂似烫伤状，烈日下更为严重。夜间可恢复，数天后整株枯死。根系表面无明显症状，但将根冠纵切，可见根冠中央有明显变褐。生育期间发病甚少，一直到草莓采收末期，青枯现象再度出现。', NULL, NULL, '&#12288;&#12288;病原为青枯假单胞菌Pseudomonas solanacearum (Smith)Smith。病原以孢子在土壤、草有茎部裂缝中越冬，在田间可通过灌溉水和雨水等传播。<br>&#12288;&#12288;适宜发病温度为22°C以上，气温、湿度、地温越高发病越严重。温度越高，湿度越大，发病就越快。草莓青枯病有两个发病高峰，一是苗期，特别是在7、8月的高温多雨季节，雨后或浇水后，发病最为严重:二是在保护地内，特别是在果实露白至上色期，温度在22- 28°C、湿度80%以上时，发病迅速。', '&#12288;&#12288;1.农业防治。育苗前整平土地，高眼栽培。在浇水或大雨后及时扣除积水，经常清除老叶、病叶和杂草。保护地载培的，应适当控制浇水量，经常通风换气，连阴天也应适当短时间通气调节。发现病林及时消除，种或保理，请除病味同围的土壤\r\n<br>&#12288;&#12288;2.药剂防治。育苗前12% 南土菌净500 倍液或99.3% 高锰酸钾1OOO倍液喷酒地面。发病初期开始喷药或灌根，常用药剂有72%农用硫酸链霉素可溶性粉剂4 000倍液，或14%络氨铜水剂350倍液，或50%琥胶肥酸可湿性粉剂500倍液。每7 〜 10天1次，连续2 〜 3次。\r\n&#12288;&#12288;1.农业防治。育苗前整平土地，高眼栽培。在浇水或大雨后及时扣除积水，经常清除老叶、病叶和杂草。保护地载培的，应适当控制浇水量，经常通风换气，连阴天也应适当短时间通气调节。发现病林及时消除，种或保理，请除病味同围的土壤\r\n<br>&#12288;&#12288;2.药剂防治。育苗前12% 南土菌净500 倍液或99.3% 高锰酸钾1OOO倍液喷酒地面。发病初期开始喷药或灌根，常用药剂有72%农用硫酸链霉素可溶性粉剂4 000倍液，或14%络氨铜水剂350倍液，或50%琥胶肥酸可湿性粉剂500倍液。每7 〜 10天1次，连续2 〜 3次。\r\n&#12288;&#12288;1.农业防治。育苗前整平土地，高眼栽培。在浇水或大雨后及时扣除积水，经常清除老叶、病叶和杂草。保护地载培的，应适当控制浇水量，经常通风换气，连阴天也应适当短时间通气调节。发现病林及时消除，种或保理，请除病味同围的土壤\r\n&#12288;&#12288;1.农业防治。育苗前整平土地，高眼栽培。在浇水或大雨后及时扣除积水，经常清除老叶、病叶和杂草。保护地载培的，应适当控制浇水量，经常通风换气，连阴天也应适当短时间通气调节。发现病林及时消除，种或保理，请除病味同围的土壤\r\n<br>&#12288;&#12288;2.药剂防治。育苗前12% 南土菌净500 倍液或99.3% 高锰酸钾1OOO倍液喷酒地面。发病初期开始喷药或灌根，常用药剂有72%农用硫酸链霉素可溶性粉剂4 000倍液，或14%络氨铜水剂350倍液，或50%琥胶肥酸可湿性粉剂500倍液。每7 〜 10天1次，连续2 〜 3次。', '/imgupload/disease/caomeiqingkubing.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (14, 48, '古毒蛾', NULL, NULL, 1, NULL, '&#12288;&#12288;古毒蛾Orgyia antiqua Linnaeus属鳞翅目，毒蛾科。为害草莓、豆类、苹果等。国内分布在黑龙江、吉林、辽宁、河北、内蒙古、山东、山西、河南、甘肃、宁夏、西藏。', '&#12288;&#12288;以幼虫啃食叶片呈缺刻和孔洞，严重时可把叶片食光。', '&#12288;&#12288;1.成虫。雌虫纺锤形，体长10一20mm,头胸部较小，体肥大，翅退化，仅有极小翅痕，体被灰黄色细毛，无鳞片，复眼球形黑色，触角丝状暗黑色，足被黄毛，爪腹面有短齿。雄虫体长10- 12mm翅展25- 30mm。体锈褐色，触角羽状。前翅黄褐色，有3条波浪形浓褐色微锯齿条纹，近臀角有一圆形白斑，中室外缘有一模糊褐色圆点，中央凹陷。<br>&#12288;&#12288;2.卵。卵圆形稍扁，直径约0.9mm，白色或淡褐色。<br>&#12288;&#12288;3.幼虫。幼虫共5- 6龄。老熟幼虫体长25- 36mm,头黑褐色，体黑灰色，有红、白灰纹，胴部有红色和淡黄毛瘤。前胸橘黄色，其两侧及第8腹节背面中央各有一束黑而长的毛。<br>&#12288;&#12288;4.蛹。雄10- 12mm,锥形。<br>&#12288;&#12288;5.茧。丝质，较薄，灰黄色。', '&#12288;&#12288;雌蛾将卵产在茧内，偶有产于茧上或附近的，每雌蛾产卵150- 300粒。初孵幼虫2天后开始取食，群集于芽、叶上取食，能吐丝下垂，借风力传播。稍大分散为害，多在夜间取食，常将叶片吃光。<br>&#12288;&#12288;东北北部每年发生1代，华北3代。以卵在茧内越冬。', NULL, '&#12288;&#12288;1.农业防治。冬春人工摘除卵块。<br>&#12288;&#12288;2.药剂防治。低龄幼虫盛期喷洒10%吡虫琳可湿性粉剂1 500倍液，或2.5% 氯氟氰菊酯乳油2 000倍液，或40%氰戊菊酯乳油1 000倍液、2.5%溴氰菊脂乳油2 000倍液。', '/imgupload/insect/gudue.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (15, 48, '梨剑纹夜蛾', NULL, NULL, 1, NULL, '&#12288;&#12288;梨剑纹夜蛾学名Acronicta rumicis L.属鳞翅目，夜蛾科，又称梨剑蛾、酸模剑纹夜蛾。为害草莓、苹果、桃、李、杏、梨等。各地均有发生。', '&#12288;&#12288;幼虫食害叶片呈缺刻或孔洞状，有的仅剩叶柄。', '&#12288;&#12288;1.成虫。体长约14mm,前翅暗棕色间以白色斑纹，基横线成一黑色短粗纹，内横线黑色弯曲，环状纹有黑边，翅外缘有一列三角形黑点。<br>&#12288;&#12288;2.卵。半球形，初产卵乳白色，渐变为赤褐色。<br>&#12288;&#12288;3.幼虫。体长约33mm,体租壮，灰褐色，背面有一列黑斑，中央有橘红色点。各节亚背线处有橘黄色斜短纹。<br>&#12288;&#12288;4.蛹。长约16mm,黑褐色。', '&#12288;&#12288;成虫有趋光性和趋化性，产卵于叶背或芽上。孵出幼虫稍停片刻卵将卵壳吃掉，然后取食嫩叶。幼虫早期群集取食，后期分散危害。老熟幼虫在叶片上吐丝结黄色薄茧化蛹。<br>&#12288;&#12288;每年发生2代。以蛹在土中越冬。5月羽化出第1代成虫，6 〜 7月为第1代幼虫为害期，幼虫常1 〜 2头分散在叶背面食害。第2代成虫8月中、下旬出现，继而产卵，孵化的幼虫维续为害，至9月下旬开始老熟，入土作茧化蛹越冬。', NULL, '&#12288;&#12288;1.农业防治。早春翻树盘，消灭越冬蛹。<br>&#12288;&#12288;2 物理防治。用糖醋液或黑光灯诱杀成虫。3.药剂防治。幼虫发生初期喷50% 辛硫磷乳油1 000 〜 1 500倍液，或80%敌敌畏乳油1 000倍液', '/imgupload/insect/lijianwenyee.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (16, 48, '红棕灰夜蛾', NULL, NULL, 1, NULL, '&#12288;&#12288;红棕灰夜蛾Polia illoba Butler属鳞翅目，夜蛾科，又称苜蓿紫夜蛾。为害茄子、豇豆、胡萝卜，甜菜、草莓、枸杞、菊、茼蒿、菜豆、草食蚕、豌豆、苜蓿、大豆等。分布黑龙江、内蒙古、河北、华东、中南地区、甘肃、江西等省。', '&#12288;&#12288;幼虫食叶皮缺刻或孔洞，严重时可把叶片食光。也可为害嫩头，花蕾和果实。', '&#12288;&#12288;1.成虫。体长15 〜 18mm,翅展38 〜 42 mm。棕色至红棕色，腹部褐色，腹端具褐色长毛。前翅上褐色剑纹粗大;环纹灰褐色，圆形；肾纹不规则，灰褐色；外线棕褐色，锯齿形。后翅大部分红棕色，基部色淡，缘毛白色。足红棕色，各足跗节均有白色环。<br>&#12288;&#12288;2.卵。卵半球状，宽0.65 mm,高0.4 mm,初产时浅绿色，后变紫褐色。<br>&#12288;&#12288；3.幼虫。初孵幼虫浅灰褐色，腹部紫红色，全体布有大而黑的毛片，足呈尺蠖状; 3龄幼虫绿色或青绿色；4龄后出现红棕色型； 6龄时基本都成为红棕色。末龄幼虫体长35 〜 45mm,头宽3 〜 3.5mm.具褐色网纹，单眼黑色，前胸盾褐色。背线和亚背线各具1纵列黄白色小圆斑，边缘棕褐色，每节每列5-7个。毛片圆形黑色;气门线黑褐色，沿上方具深福色圆斑;气门下线浅黄色至黄色。<br>&#12288;&#12288; 4.蛹。长18〜20mm,宽6 〜 7mm,深褐色，蛹体较粗糙，臀棘短粗，末端分叉。', '&#12288;&#12288;成虫有趋光性。1- 2龄幼虫群聚在叶背取食叶肉，有的钻入花蕾中取食； 3龄后分散；4龄时出现假死性，白天多栖息在叶背或心叶上；5 〜 6龄进人暴食期，末龄幼虫食毁草莓的嫩头、蕾花、幼果等。老熟幼虫在土内3 〜 6cm处化蛹。', NULL, '&#12288;&#12288;1.物理防治。成片安置黑光灯，进行测报和防治。<br>&#12288;&#12288; 2药剂防治。幼虫发生啊可喷洒5%氯氰菊脂乳油1 000信液，或1.8%阿维菌素乳油300倍液，每7天左右1次，连续1 〜 2次。', '/imgupload/insect/hongzonghuiyee.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (17, 48, '麻皮蝽', NULL, NULL, 1, NULL, '&#12288;&#12288;麻皮蝽 Erthesina fullo Thunberg属半翅目，蝽科。又称黄霜蝽、黄斑蝽。为害草莓、苹果、梨、山楂、李、桃、杏、樱桃、葡萄、枣、柿、柑橘、石榴、龙眼多种植物，各地均有发生。', '&#12288;&#12288;成虫飞行力强，有假死性，受惊扰时分泌臭液。每年发生1代。以成虫于草丛或树洞，皮裂缝及枯枝落叶下及墙缝，屋檐下越冬。', '&#12288;&#12288;1.成虫。体长21- 245m.宽约10mm..体背面黑褐色。密布黑色刻点和细碎不规则黄斑。头部较狭长，侧叶与中叶末端约等长，有一条黄白色线从中片延伸至小质片<br>&#12288;&#12288;2.卵。长2mm，宽约1mm.竖立，近球形。<br>&#12288;&#12288;3.若虫。共5龄。各龄若虫体扁，洋梨形，前端较窄，后端宽圆，全身侧缘具浅黄色狭边。', '&#12288;&#12288;成、若虫吸食叶片、嫩梢及果实汁液，引起刺吸点以上叶脉变黑，叶肉组织颜色变暗枯死。', NULL, '&#12288;&#12288;1.农业防治。秋末初春，在成虫越冬场所，人工捕杀成虫。利用成虫、若虫喜白天活动、正午活动高峰时机人工捕杀。<br>&#12288;&#12288;2.药剂防治。在成虫产卵前进行较好。药剂可选用1.8%阿维菌素乳油3 000倍液，或50%氰戊•辛硫磷乳油3 000倍液，或20% 氯氰菊酯乳油3 000倍液，或2.5%溴氰菊脂乳油3 000倍液。或2.5%氯氟氰菊脂乳油3000倍液，或20%甲氰菊脂乳油3 000信液。或90% 美曲膦脂晶体800倍液。', '/imgupload/insect/mapichun.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (18, 48, '东北大黑鳃金龟', NULL, NULL, 1, NULL, '&#12288;&#12288;东北大黑鳃金龟Holotrichia diomphalia Bates,属鞘翅目，鳃金龟科。幼虫称蛴螬，又称白地蚕、白土蚕。为害杂食性，各地均有发生。', '&#12288;&#12288;幼虫食害各种蔬菜苗根，引起幼苗致死，造成缺苗断垄。成虫仅食害树叶及部分作物叶片。', '&#12288;&#12288;1.成虫。体长16 〜 21m，宽8〜11mm，黑色或黑褐色，具光泽。<br>&#12288;&#12288;2.卵。初产长椭圆形，白色稍带黄绿色光泽; 发育后期呈圆球形。<br>&#12288;&#12288;3.幼虫。三龄幼虫体长35 〜 45 mm,头宽4.9 〜 5.3mm。头部黄褐色，胴部乳白色。头部前顶刚毛每侧3根，成一纵列。肛门孔呈三射裂缝状，肛腹片后部复毛区散生钩状刚毛。<br>&#12288;&#12288;4.蛹。为裸蛹，初期白色，渐转红褐色。', '&#12288;&#12288;成虫有假死性和趋光性，并对未腐熟的厩肥有强烈趋性，昼间藏在土壤中，晚8- 9时为取食、交配活动盛期。产于松软湿润的土壤内，以水浇地最多。蛴螬始终在地下活动，与土壤温湿度关系密切，当10cm 土温达5°C时开始上升至表土层，13 〜 18°C时活动最盛，23°C以上则往深土中移动。<br>&#12288;&#12288;1- 2 年发生1代。以幼虫或成虫在土壤中越冬。每年的5 〜 7月，成虫大量出现。土壤湿润时，蛴螬的活动性强，尤其小雨连绵的天气为害较重。', NULL, '&#12288;&#12288;1.农业防治。在深秋或初冬翻耕土地，能杀灭部分害虫。施用腐热的厩肥。蛴螬发生区，在不影响作物生长发育的前提下，对于灌溉要合理地加以控制。<br>&#12288;&#12288;2.药剂防治。卵孵盛期可用48% 毒死蜱乳油1 000倍液灌根防治。', '/imgupload/insect/dongbeidaheisaijingui.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (19, 48, '同型巴蜗牛', NULL, NULL, 1, NULL, '&#12288;&#12288;同型巴蜗牛Bradybaena similaris (Ferussac)属腹足纲，柄眼目，巴蜗牛科。为害韭菜、白菜、萝卜、甘蓝、花椰菜、菜豆、莴苣、芹菜、菠菜等多种蔬菜。黄河流域以南均有发生，保护地蔬菜上为害日益严重。', '&#12288;&#12288;初孵幼螺只取食叶肉，留下表皮，稍大个体则用齿舌将叶、茎舐磨成小孔或将其吃断。<br>&#12288;&#12288;每年发生1代。以成贝或幼贝在菜田作物根部，草堆、土缝里越冬。每年3月下旬越冬蜗牛陆续恢复活动，5 - 6月为交配产卵盛期。6 - 7月形成全年中第1个幼贝高峰。8 ~ 9月高温干旱部分幼贝死亡，到了9月下旬后，气温下降，蜗牛又恢复活动。喜阴湿天气，如遇雨天，昼夜活动为害: 而在干旱情况下，白天潜伏，夜间活动。当温度在11.5- 18.5C,土填含水量在20% 一 30%时对其取食活动生长发育有利，若温度高时，25°C以上，土壤含水量在10%-15% 以下或40%以上，会引起死亡或生长受到抑制。', '&#12288;&#12288;1.成虫。成贝头部发达，头上具有两对触角，眼在触角的顶端，口位于头部腹面，并具有触唇，足在身体腹面; 螺壳呈扁圆球形，壳高9.1 - 13.6mm，壳宽11.2- 18.4mm,螺层数有5 - 6层，壳质坚硬，黄褐色或红褐色，螺旋部低矮，体层较宽大，周缘中部有一条褐色带。壳口马蹄形，脐孔呈圆孔状。<br>&#12288;&#12288;2.卵。圆球形，直径约2mm，乳白色有光泽，近孵化时变成土黄色。<br>&#12288;&#12288;3.幼虫。初孵幼贝螺壳高0.8 - 1.7mm 有1 - 2个螺层，5个月后螺层增至4- 5层，9个月后增至5 - 6 层。', NULL, NULL, '&#12288;&#12288;1.农业防治。清洁田园、铲除田边地头杂草，并撒上生石灰，减少蜗牛的滋生地。在每年5 - 6 月蜗牛产卵期间，中耕松土，可杀灭卵粒和幼贝，11月结合冬耕，使一部分越冬成贝或幼贝翻到地面冻死，减少虫口密度。人工捕捉，利用树叶、杂草、菜叶等诱集堆，天亮前蜗牛潜伏在诱集堆下，再集中捕捉。<br>&#12288;&#12288;2.药剂防治。必要时，每亩可用2%甲硫威颗粒剂500g，或6% 四聚乙醛杀螺颗粒毒饵500g，或6%速灭•硫酸铜颗粒剂500g，或6% 甲萘•四聚颗粒剂700g,在日落后均匀撒在蔬菜行间及基部。', '/imgupload/insect/tongxingbawoniu.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (20, 48, '灰巴蜗牛', NULL, NULL, 1, NULL, '&#12288;&#12288;灰巴蜗牛Bradybaena ravid( Benson) 属腹纲，柄眼目，巴蜗牛科，又称蜒蚰螺。保护地蔬菜迅速发展为蜗牛的栖息和越冬提供了适宜的环境，蜗牛的发生有加重的趋势。为害草梅、甘蓝、花椰菜、白菜、萝卜，豆类，马铃薯等蔬菜，各地均有发生。', '&#12288;&#12288;主要取食蔬菜幼苗、叶、地下块茎及果实。蜗牛将蔬菜幼苗咬断，造成缺苗断垄，甚至整块地仅剩个别植株。为害叶片，造成缺刻、孔洞，严重时仅余叶脉。', '&#12288;&#12288;1.成贝。贝壳中等大小，呈圆球形，壳质稍厚，壳高19mm.宽21mm,有5- 6个螺层，顶部几个螺层增长缓慢、略膨胀，体螺层急骤增长、膨大。壳面黄褐色或琥珀色，并具有细致而稠密的生长线和螺纹。壳顶尖，缝合线深，触角两对，其中后触角比较长，其顶端长有黑色眼睛。壳口呈椭圆形，口缘完整，略外折，锋利，易碎。轴缘在脐孔处外析，略遮盖脐孔，脐孔狭小，呈缝隙状。2.卵。圆球形，直径2mm，乳白色，有光泽，逐渐变成淡黄色，近孵化时，变成土黄色。3.幼贝。浅褐色，体较小，形态特征与成贝相似，', '&#12288;&#12288;每年繁殖1、2次卵产于草根、农作物根部，土壤中、石块下或土缝里。', NULL, '&#12288;&#12288;参见同型巴蜗牛。', '/imgupload/insect/huibawoniu.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (21, 48, '薄球蜗牛', NULL, NULL, 1, NULL, '&#12288;&#12288;薄球蜗牛Truiciola ravida (Benson)属肤足纲，柄眼目，又称刚蜗。为害草莓、白菜、豆类、玉米、花卉、果树等。', '&#12288;&#12288;靠舌头上的锉形组织和舌头两侧角质带状组织上布满的细小牙齿磨碎植物的茎，叶或根。<br>&#12288;&#12288;上海、浙江一带每年发生1代，11月下旬以成贝和幼贝在田埂土缝、残株落叶中越冬，3月上中旬开始活动。', '&#12288;&#12288;1.成员体灰黄褐色，螺壳上散生灰黑色斑纹，具5层螺层，头部有长、短触角各1对。<br>&#12288;&#12288;2.幼贝形态和颜色与成贝极相似，体型略小，螺层多在4层以下。<br>&#12288;&#12288;3.卵。圆球形，初为白色，孵化前变为灰黄色，有光泽。', '&#12288;&#12288;白天潜伏，傍晚或清晨取食，遇有阴雨天多整天栖息在植株上。4月下旬列5月上中旬开始交配，卵成堆产在植株根茎部的湿土中，初产的卵表面具黏液，后干燥成块状。初孵幼贝群集为害，后分散。遇有高温干燥条件，蜗牛常把壳口封住，潜伏在潮湿的土缝中或茎叶下，待条件适宜时外出取食，11月中下旬又开始越冬。', NULL, '&#12288;&#12288;1.农业防治。人工捕捉，集中杀灭。<br>&#12288;&#12288;2.药剂防治。用茶籽饼粉3kg撒施或用茶籽饼1 - 1.5kg加水100g浸泡24小时后，取其滤液喷雾;也可用50%辛硫磷乳油1 000倍液喷雾。每亩用8%四聚乙能颗粒剂1.5- 2kg。碾碎后并细或饼用5-7kg.于天气温暖，土表干燥的傍晚撒在受害体附近根部的行间，接触药剂的蜗牛分泌大量黏液而死亡。', '/imgupload/insect/baoqiuwoniu.jpg', 0, 0, 0);
INSERT INTO `disease` VALUES (22, 48, '野蛞蝓', NULL, NULL, 1, NULL, '&#12288;&#12288;野蛞蝓Deroceras reticaulatum (Muller)属肺螺亚纲，眼目，蛞蝓科，又称无壳蜒蚰螺、鼻涕虫。在北方棚室内的发生为害逐年加重。为害十字花科、茄科、豆科蔬菜等。国内广泛分布。', '&#12288;&#12288;蛞蝓以齿舌刮食幼芽、嫩叶、嫩茎，幼苗受害可造成缺苗断垄，严重时成片被毁;成株期叶片出现缺刻或孔洞，严重时仅残存叶脉，植株受其排泄的粪便污染，易诱发菌类侵染而导致腐烂，降低产量和质量。', '&#12288;&#12288;体长约50 mm,扭曲不对称;头清晰可见，眼生在触须顶端，体浅黄色，有深褐点条网络，套腔两侧圆形且有深色小点和斑纹；套腔和体躯不分区带；内脏囊被套腔盖住；呼吸孔的边缘灰色，同心具脊的套腔中心点位于中线右侧;脊棱短且断开，取面具爬行足，黏液白色至乳白色。', '&#12288;&#12288;雌体交配后8 〜 10天产卵，每雌产卵300粒，土壤含水量60% 〜 85%利其生殖。黏重土，低洼处蛞蝓多。喜温暖、潮湿环境，畏忌阳光，裸露在干燥条件下即死亡。成、幼体适宜活动的温度15 〜 25°C.相对湿度85%以上，春、秋多露或雨后发生为害重，夏季高温干早或冬季潜入隐蔽处土下休眠。蛞蝓白天潜藏作物根部湿土下或阴暗处，夜晚活动取食，阴雨天可昼夜为害。', NULL, '&#12288;&#12288;1.农业防治。棚室通风透光，清除田间及棚室周围杂草。用杂草、菜叶等在棚室内做诱集堆，天亮前集中捕捉。<br>&#12288;&#12288;2.药剂防治。蔬菜出苗或移栽后，在蛞蝓发生初期，每亩用6%四聚乙醛颗粒剂500g，拌细干土15 〜 20kg.于傍晚均匀撒在受害植株的行间垄上。', '/imgupload/insect/yekuoyu.jpg', 0, 0, 0);
COMMIT;

-- ----------------------------
-- Table structure for disease_option
-- ----------------------------
DROP TABLE IF EXISTS `disease_option`;
CREATE TABLE `disease_option` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `plant_id` int(11) NOT NULL COMMENT '植物id',
  `question_id` int(11) NOT NULL COMMENT '对应问题id',
  `option_description` text NOT NULL COMMENT '形状描述',
  `option_image` text COMMENT '图片，多个图片路径以#隔开',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '更新时间',
  `delete_time` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for disease_question
-- ----------------------------
DROP TABLE IF EXISTS `disease_question`;
CREATE TABLE `disease_question` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `plant_id` int(11) NOT NULL COMMENT '植物id',
  `question_name` text NOT NULL COMMENT '问题名称',
  `question_sequence` text NOT NULL COMMENT '第几题',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '更新时间',
  `delete_time` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for plant
-- ----------------------------
DROP TABLE IF EXISTS `plant`;
CREATE TABLE `plant` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `plant_name` varchar(511) NOT NULL COMMENT '植物名称',
  `plant_alias` text COMMENT '植物别名，多个别名以#隔开',
  `plant_latin_name` text COMMENT '植物拉丁文',
  `plant_image` text COMMENT '图片，多个图片路径以#隔开',
  `plant_family` varchar(511) DEFAULT NULL COMMENT '科',
  `plant_genus` varchar(511) DEFAULT NULL COMMENT '属',
  `plant_description` text COMMENT '描述',
  `plant_xgsc` text COMMENT '相关诗词',
  `plant_jzgy` text COMMENT '价值公用',
  `plant_fbdq` text COMMENT '分布地区',
  `plant_yhjs` text COMMENT '养护技术',
  `plant_bxtz` text COMMENT '表型特征',
  `plant_hksj` text COMMENT '花开时节',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '更新时间',
  `delete_time` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=51 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of plant
-- ----------------------------
BEGIN;
INSERT INTO `plant` VALUES (6, '虎尾兰', '金边虎尾兰', 'Sansevieria trifasciata', 'https://api.aiplants.cn/resource/1/%E8%99%8E%E5%B0%BE%E5%85%B0/1460133.jpg#https://api.aiplants.cn/resource/1/%E8%99%8E%E5%B0%BE%E5%85%B0/1557413.jpg#https://api.aiplants.cn/resource/1/%E8%99%8E%E5%B0%BE%E5%85%B0/2967619.jpg#https://api.aiplants.cn/resource/1/%E8%99%8E%E5%B0%BE%E5%85%B0/741201.jpg', '龙舌兰科', '虎尾兰属', '多年生草本观叶植物，品种较多，株形和叶色变化较大，对环境的适应能力强。主要品种有金边虎尾兰、银脉虎尾兰。', '春意盎然\n身披绿衣衫\n我是利箭指云端\n我是凤尾降人间\n主人珍爱我\n笑称孔雀开屏\n讨人欢！　\n--《虎尾兰》', '为常见的家内盆栽观叶植物。适合布置装饰书房、客厅、办公场所，可供较长时间欣赏。此外，其叶纤维强韧，可供编织用。', '中国各地有栽培。', '适应性强，性喜温暖湿润，耐干旱，喜光又耐阴。对土壤要求不严，以排水性较好的砂质壤土较好。其生长适温为20-30℃，越冬温度为10℃。', '有横走根状茎。叶基生，常1-2枚，也有3-6枚成簇的，直立，硬革质，扁平，长条状披针形，长30-70 (-120) 厘米，宽3-5(-8)厘米，有白绿色不晰荣绿色相间的横带斑纹，边缘绿色，向下部渐狭成长短不等的、有槽的柄。花葶高30-80厘米，基部有淡褐色的膜质鞘；花淡绿色或自色，每3-8朵簇生，排成总状花序；花梗长5-8毫米，关节位于中部；花被长1.6-2.8厘米，管与裂片长度约相等。浆果直径约7-8毫米。', '花期11-12月。', 1527171662, 1527172225, 0);
INSERT INTO `plant` VALUES (7, '琉璃殿', '', 'Haworthia limifolia', '', '阿福花科', '十二卷属', '在我国栽培已久，叶形奇特，株形端庄大方，是室内装饰的理想种类。', '', '琉璃殿的叶盘排列和叶面横生的疣突都非常特殊，这在多肉植物中很少见。独特的叶姿若配精制的盆钵装饰，摆放居室的小环境，可起到意想不到的装饰效果。', '原产于南非和西南非，世界多地可栽培。', '喜温暖干燥和阳光充足环境,较耐寒，耐干旱和半阴，不耐水湿和强光暴晒。以肥沃、疏松的沙壤土为宜。生长期适宜温度白天是18至24℃，冬季温度不低于5℃。', '多肉植物，肉质叶排成莲座状叶盘，株幅8至15厘米，叶有部分叠生，全部向一侧偏转，使叶盘看起来像旋转的风车。叶卵圆状三角形，先端急尖，正面凹，背面圆凸，密布由小疣组成的瓦楞状突起横条，酷似一排排的琉璃瓦。叶色深绿或灰绿，有深浅不一、宽窄不等的黄色纵条纹，有时整个叶片都呈黄色。花序长10至40厘米，白灰相间的小百合花，蜜腺发达，花后2个月左右种子成熟，种囊分3室，每室有黑色瘦果种子3至4粒。', '盛花期为4至6月、9至10月，如果栽培条件好，植株生长健壮，其他季节也能开花。', 1527171685, 1527172229, 0);
INSERT INTO `plant` VALUES (8, '金边虎尾兰', '金边虎皮兰', 'Sansevieria trifasciata var. laurentii', 'https://api.aiplants.cn/resource/1/%E9%87%91%E8%BE%B9%E8%99%8E%E5%B0%BE%E5%85%B0/1262304.jpg#https://api.aiplants.cn/resource/1/%E9%87%91%E8%BE%B9%E8%99%8E%E5%B0%BE%E5%85%B0/1557450.jpg#https://api.aiplants.cn/resource/1/%E9%87%91%E8%BE%B9%E8%99%8E%E5%B0%BE%E5%85%B0/1557455.jpg#https://api.aiplants.cn/resource/1/%E9%87%91%E8%BE%B9%E8%99%8E%E5%B0%BE%E5%85%B0/1888955.jpg', '龙舌兰科', '虎尾兰属', '多年生草本植物有横走根状茎，是一种能净化室内环境的草本观叶植物。', '绿蚁新醅酒，红泥小火炉。\n晚来天欲雪，能饮一杯无？\n--《问刘十九》朝代：唐 作者：白居易', '供观赏。叶纤维强韧，可供编织用。', '我国各地有栽培。', '浇水要适量，掌握宁干勿湿的原则。喜光，所以一定要让它时常接触阳光，最好是能放在可以直接被照射的地方，对温度的要求颇高，适宜生长温度为20～30℃，在冬天是最低不要低于10℃。', '有横走根状茎。叶基生，常1-2枚，也有3-6枚成簇的，直立，硬革质，扁平，长条状披针形，长30-70 (-120) 厘米，宽3-5(-8)厘米，有白绿色不晰荣绿色相间的横带斑纹，边缘绿色，向下部渐狭成长短不等的、有槽的柄。花葶高30-80厘米，基部有淡褐色的膜质鞘；花淡绿色或自色，每3-8朵簇生，排成总状花序；花梗长5-8毫米，关节位于中部；花被长1.6-2.8厘米，管与裂片长度约相等。浆果直径约7-8毫米。', '花期11-12月。', 1527171835, 1527238456, 0);
INSERT INTO `plant` VALUES (9, '子宝', '', 'Gasteria gracilis var. minima', 'https://api.aiplants.cn/resource/1/%E5%AD%90%E5%AE%9D/11_450x360.jpg#https://api.aiplants.cn/resource/1/%E5%AD%90%E5%AE%9D/2744693.jpg#https://api.aiplants.cn/resource/1/%E5%AD%90%E5%AE%9D/293672.jpg#https://api.aiplants.cn/resource/1/%E5%AD%90%E5%AE%9D/442944AA390C0CF6.jpg', '阿福花科', '鲨鱼掌属', '是一种观赏多肉植物。', '', '叶形叶色较美，有一定的观赏价值；盆栽可放置于电视、电脑旁，可吸收辐射，亦可栽植于室内以吸收甲醛等物质，净化空气。', '多地可栽培。', '喜欢半阴、通风良好的地方，过多的日照会使原本绿色的叶片变的更深， 喜疏松肥沃、排水良好的砂质 壤土做栽培基质，如果有条件，所用盆土可由腐叶、 细砂、园土配制；较耐寒，越冬温度不低于5℃；夏季不能暴晒，子宝暴晒会灼伤，晒出黑斑及叶片薄、不鲜绿等不良生长状况，较耐旱。', '叶肉质较厚，像舌头，叶面光滑，带有白色 斑点，同种类还有锦斑变异品种，叶片中间会出现白色斑纹，是比较容易发生锦斑变异的品种。叶面 2～5厘米长，1～2.5厘米宽，暴晒后叶面呈红色。子宝叶片美丽，有带锦与不带锦的，普通的为全绿，不带锦；两种颜色以上为带锦，称子宝锦，颜色不一，各有特色。', '冬季至春季为开花旺季。', 1527171868, 1527172232, 0);
INSERT INTO `plant` VALUES (10, '樱麒麟', '', 'Pereskia bleo', 'https://api.aiplants.cn/resource/1/%E6%A8%B1%E9%BA%92%E9%BA%9F/2089489.jpg#https://api.aiplants.cn/resource/1/%E6%A8%B1%E9%BA%92%E9%BA%9F/2151405.jpg#https://api.aiplants.cn/resource/1/%E6%A8%B1%E9%BA%92%E9%BA%9F/2865730.jpg#https://api.aiplants.cn/resource/1/%E6%A8%B1%E9%BA%92%E9%BA%9F/2922685.jpg', '仙人掌科', '木麒麟属', '常绿半蔓藤植物，原产哥伦比亚，是仙人掌科植物的原始种，也是唯一一种带叶的仙人掌类植物。\n', '', '既可盆栽观赏，也可加工制作成各种艺术造型的花篱、花架，中国南方地区常用其作墙垣绿化，效果极佳。', '中国华南大部分地区均可露地栽培，北方可盆栽观赏。', '生性强健，喜温暖湿润的环境，抗旱力强，不耐寒，既喜阳也能耐阴，耐瘠薄，对土质要求不是很严。使用一般的田园土栽培，就能枝繁叶茂。中国华南大部分地区均可露地栽培，北方可盆栽观赏。', '外形呈灌木或藤本状。株高一般6~7米，最高可达15米。枝条较粗壮，茎部光润，\n节部明显，虽然多肉，但易木质化。叶基腋处生有数枚深褐色长锐刺。叶片多肉质而肥厚，有短柄，表面光滑具蜡质，全缘，叶亮绿色。总状花序顶生，花淡紫红色，辐射状，直径5~6厘米。', '花期5~10月。', 1527172228, 1527172228, 0);
INSERT INTO `plant` VALUES (11, '柚', '抛、文旦', 'Citrus maxima', 'https://api.aiplants.cn/resource/1/%E6%9F%9A/1097542.jpg#https://api.aiplants.cn/resource/1/%E6%9F%9A/1111547.jpg#https://api.aiplants.cn/resource/1/%E6%9F%9A/1295389.jpg#https://api.aiplants.cn/resource/1/%E6%9F%9A/1443295.jpg', '芸香科', '柑橘属', ' 是一种经济树种。叶质颇厚，色浓绿，阔卵形或椭圆形；总状花序，有时兼有腋生单花；花蕾淡紫红色，稀乳白色。', '', '柚肉中含有非常丰富的维生素C等营养成分，可以降血糖、减肥、养容。', '中国长江以南各地，最北限见于河南省信阳及南阳一带，全为栽培。', '柚对温度和土壤没什么特别高的要求，简单粗暴的照料它，也能长得很好，只要注意不要强光照射就行了。', '乔木。嫩枝、叶背、花梗、花萼及子房均被柔毛，嫩叶通常暗紫红色，嫩枝扁且有棱。叶质颇厚，色浓绿，阔卵形或椭圆形，连冀叶长9-16厘米，宽4-8厘米，或更大，顶端钝或圆，有时短尖，基部圆，翼叶长2-4厘米，宽0.5-3厘米，个别品种的翼叶甚狭窄。总状花序，有时兼有腋生单花；花蕾淡紫红色，稀乳白色；花萼不规则5-3浅裂；花瓣长1.5-2厘米；雄蕊25-35枚，有时部分雄蕊不育；花柱粗长，柱头略较子房大。果圆球形，扁圆形，梨形或阔圆锥状，横径通常10厘米以上，淡黄或黄绿色，杂交种有朱红色的，果皮甚厚或薄，海绵质，油胞大，凸起，果心实但松软，瓢囊10-15或多至19瓣，汁胞白色、粉红或鲜红色，少有带乳黄色；种子多达200余粒，亦有无子的，形状不规则，通常近似长方形，上部质薄且常截平，下部饱满，多兼有发育不全的，有明显纵肋棱，子叶乳白色，单胚。', '花期4-5月，果期9-12月。', 1527172256, 1527422962, 0);
INSERT INTO `plant` VALUES (12, '沙田柚', '', 'Citrus maxima cv. Shatian', 'https://api.aiplants.cn/resource/1/%E6%B2%99%E7%94%B0%E6%9F%9A/1007116.jpg#https://api.aiplants.cn/resource/1/%E6%B2%99%E7%94%B0%E6%9F%9A/1504936.jpg#https://api.aiplants.cn/resource/1/%E6%B2%99%E7%94%B0%E6%9F%9A/1504945.jpg#https://api.aiplants.cn/resource/1/%E6%B2%99%E7%94%B0%E6%9F%9A/955021.jpg', '', '', '乔木。嫩枝、叶背、花梗、花萼及子房均被柔毛，嫩叶通常暗紫红色，嫩枝扁且有棱。叶质颇厚，色浓绿，阔卵形或椭圆形。果梨形或葫芦形，果顶略平坦，有明显环圈及放射沟，蒂部狭窄而延长呈颈状，果肉爽脆，味浓甜，但水分较少，种子颇多。', '', '沙田柚不但营养价值高，而且还具有健胃、润肺、补血、清肠、利便、消食、解酒毒等功效。', '长江以南各地，最北限见于河南省信阳及南阳一带，全为栽培。', '果成熟过程中要注意水肥管理。施肥原则是重施花前、果后肥，巧施稳果、壮果肥，辅以根外追肥。水分原则是春湿、夏排、秋灌、冬控。全年做到湿而不涝，旱而不缺科学用水。', '乔木。嫩枝、叶背、花梗、花萼及子房均被柔毛，嫩叶通常暗紫红色，嫩枝扁且有棱。叶质颇厚，色浓绿，阔卵形或椭圆形，连冀叶长9-16厘米，宽4-8厘米，或更大，顶端钝或圆，有时短尖，基部圆，翼叶长2-4厘米，宽0.5-3厘米，个别品种的翼叶甚狭窄。总状花序，有时兼有腋生单花；花蕾淡紫红色，稀乳白色；花萼不规则5-3浅裂；花瓣长1.5-2厘米；雄蕊25-35枚，有时部分雄蕊不育；花柱粗长，柱头略较子房大。果圆球形，扁圆形，梨形或阔圆锥状，横径通常10厘米以上，淡黄或黄绿色，杂交种有朱红色的，果皮甚厚或薄，海绵质，油胞大，凸起，果心实但松软，瓢囊10-15或多至19瓣，汁胞白色、粉红或鲜红色，少有带乳黄色；种子多达200余粒，亦有无子的，形状不规则，通常近似长方形，上部质薄且常截平，下部饱满，多兼有发育不全的，有明显纵肋棱，子叶乳白色，单胚。', '花期4-5月，果期9-12月。 ', 1527172266, 1527422964, 0);
INSERT INTO `plant` VALUES (13, '榴莲', '', 'Durio zibethinus', 'https://api.aiplants.cn/resource/1/%E6%A6%B4%E8%8E%B2/1433993.jpg#https://api.aiplants.cn/resource/1/%E6%A6%B4%E8%8E%B2/173231.jpg#https://api.aiplants.cn/resource/1/%E6%A6%B4%E8%8E%B2/2274790.jpg#https://api.aiplants.cn/resource/1/%E6%A6%B4%E8%8E%B2/701711.jpg', '锦葵科', '榴莲属', '榴莲在泰国最负有盛名，被誉为“水果之王”。它的气味浓烈、爱之者赞其香，厌之者怨其臭。', '何药能医肠九回。榴莲不似蜀当归。\n却簪征帽解戎衣。泪下猿声巴峡里，\n眼荒鸥碛楚江涯。梦魂只傍故人飞。\n--《浣溪沙》年代： 宋 作者：王质', '榴莲是热带著名水果之一，果未成熟时供蔬食。种子可炒食。', '广东海南栽培。', '榴莲属于纯热带作物，榴莲的物候学特征生长所在地日平均温度22℃以上。无霜冻的地区可以种植，中国海南省和云南省的部分地区可以发展。 因为榴莲要终年高温的气候才能生长结实，即使在赤道地区，海拔600m以上的高地，由于气温下降，也不能种植榴莲或不能结果。', '常绿乔木，高可达25米，幼枝顶部有鳞片。托叶长1.5-2厘米，叶片长圆形，有时倒卵状长圆形，短渐尖或急渐尖，基部圆形或钝，两面发亮，上面光滑，背面有贴生鳞片，侧脉10-12对，长10-15厘米，宽3-5厘米；叶柄长1.5-2.8厘米，聚伞花序细长下垂，簇生于茎上或大枝上，每序有花3-30朵；花蕾球形；花梗被鳞片，长2-4厘米。苞片托住花萼，比花萼短，萼筒状，高2.5-3厘米，基部肿胀，内面密被柔毛，具5-6个短宽的萼齿；花瓣黄白色，长3.5-5厘米，为萼长的2倍，长圆状匙形，后期外翻；雄蕊5束，每束有花丝4-18，花丝基部合生1/4-1/2；蒴果椭圆状，淡黄色或黄绿色，长15-30厘米，粗13-15厘米，每室种子2-6，假种皮白色或黄白色，有强烈的气味。', '花果期6-12月。', 1527172268, 1527258690, 0);
INSERT INTO `plant` VALUES (14, '郁金香', '', 'Tulipa gesneriana', 'https://api.aiplants.cn/resource/1/%E9%83%81%E9%87%91%E9%A6%99/107606.jpg#https://api.aiplants.cn/resource/1/%E9%83%81%E9%87%91%E9%A6%99/124587.jpg#https://api.aiplants.cn/resource/1/%E9%83%81%E9%87%91%E9%A6%99/124592.jpg#https://api.aiplants.cn/resource/1/%E9%83%81%E9%87%91%E9%A6%99/128663.jpg', '百合科', '郁金香属', '草本植物，是土耳其、哈萨克斯坦、荷兰的国花，被称为世界花后，成为代表时尚和国际化的一个符号。', '兰陵美酒郁金香，玉碗盛来琥珀光。  \n但使主人能醉客，不知何处是他乡。\n--《客中行》朝代：唐 作者：李白', '化湿辟秽。主脾胃湿浊；胸脘满闷；呕逆腹痛；口臭苔腻。', '中国的东北地区等地。', '以沙壤土为好，土壤酸碱度以中性偏碱为好，栽前要施足基肥。', '鳞茎皮纸质，内面顶端和基部有少数伏毛。叶3-5枚，条状披针形至卵状披针形。花单朵顶生，大型而艳丽；花被片红色或杂有白色和黄色，有时为白色或黄色，长5-7厘米，宽2-4厘米。6枚雄蕊等长，花丝无毛；无花柱，柱头增大呈鸡冠状。', '花期4-5月。', 1527172270, 1527172270, 0);
INSERT INTO `plant` VALUES (15, '风信子', '洋水仙，五色水仙', 'Hyacinthus orientalis', 'https://api.aiplants.cn/resource/1/%E9%A3%8E%E4%BF%A1%E5%AD%90/1920995.jpg#https://api.aiplants.cn/resource/1/%E9%A3%8E%E4%BF%A1%E5%AD%90/2057911.jpg#https://api.aiplants.cn/resource/1/%E9%A3%8E%E4%BF%A1%E5%AD%90/2161826.jpg#https://api.aiplants.cn/resource/1/%E9%A3%8E%E4%BF%A1%E5%AD%90/2684702.jpg', '天门冬科', '风信子属', '多年草本球根类植物。', '', '风信子是家庭中很常见的装饰花，美丽且方便养护。风信子根部的球茎有毒，误食会导致头晕、腹泻，家里有小朋友的一定要特别注意了！', '全国各地。', '风信子需要在阳光充足的环境下生长，否则很难开花。水养风信子要放到没有阳光的地方让它发根，1个月发出许多白根后再移到阳光下，让它开花。水养风信子时，水很重要，每隔3-4天需要换一次水。', '风信子是多年草本生球根类植物，鳞茎球形或扁球形，有膜质外皮，外被皮膜呈紫蓝色或白色等，皮膜颜色与花色成正相关。未开花时形如大蒜。叶4-9枚，狭披针形，肉质，基生，肥厚，带状披针形，具浅纵沟，绿色有光。花茎肉质，花葶高15-45厘米，中空，端着生总状花序；小花10-20朵密生上部，多横向生长，少有下垂，漏斗形，花被筒形，上部四裂，花冠漏斗状，基部花筒较长，裂片5枚。向外侧下方反卷。根据其花色，大致分为蓝色、粉红色、白色、鹅黄、紫色、黄色、绯红色、红色等八个品系。原种为浅紫色，具芳香。蒴果。', '花期早春，自然花期3-4月。', 1527172271, 1527172271, 0);
INSERT INTO `plant` VALUES (16, '皂柳', '红心柳', 'Salix wallichiana', 'https://api.aiplants.cn/resource/1/%E7%9A%82%E6%9F%B3/1000503.jpg#https://api.aiplants.cn/resource/1/%E7%9A%82%E6%9F%B3/2682533.jpg#https://api.aiplants.cn/resource/1/%E7%9A%82%E6%9F%B3/406364.jpg#https://api.aiplants.cn/resource/1/%E7%9A%82%E6%9F%B3/575576.jpg', '杨柳科', '柳属', '灌木或乔木。小枝红褐色、黑褐色或绿褐色，叶披针形，长圆状披针形，卵状长圆形，狭椭圆形，花序先叶开放或近同时开放。', '', '枝条可编筐篓，板材可制木箱（湖北西部），根入药，治风湿性关节炎。', '内蒙古、河北、山西、陕西、甘肃、青海、浙江、湖南、湖北、四川、贵州、云南、西藏。', '生于山谷溪流旁，林缘或山坡。', '灌木或乔木。小枝红褐色、黑褐色或绿褐色，初有毛后无毛。芽卵形，有棱，先端尖，常外弯，红褐色或栗色，无毛。叶披针形，长圆状披针形，卵状长圆形，狭椭圆形，长4-8（10）厘米，宽1-2.5(3)厘米，先端急尖至渐尖，基部楔形至圆形，上面初有丝毛，后无毛，平滑，下面有平伏的绢质短柔毛或无毛，浅绿色至有白霜，网脉不明显，幼叶发红色；全缘，萌枝叶常有细锯齿；上年落叶灰褐色；叶柄长约1厘米；托叶小比叶柄短，半心形，边缘有牙齿。花序先叶开放或近同时开放，无花序梗（生在萌枝上的或顶端生长势减弱的枝上的花序常有花序梗，并有2-3个小叶）；雄花序长1.5-2.5(3)厘米，粗1-1.3(1.5)厘米；雄蕊2，花药大，椭圆形，长0.8-1毫米，黄色，花丝纤细，离生，长5-6毫米，无毛或基部有疏柔毛；苞片赭褐色或黑褐色，长圆形或倒卵形，先端急尖，两面有白色长毛或外面毛少；腺1，卵状长方形；雌花序圆柱形，或向上部渐狭（下部花先开放），2.5-4厘米长，粗约1-1.2厘米，果序可伸长至12厘米，粗1.5厘米；子房狭圆锥形，长3-4毫米，密被短柔毛，子房柄短或受粉后逐渐伸长，有的果柄可与苞片近等长，花柱短至明显，柱头直立，2-4裂；苞片长圆形，先端急尖，赭褐色或．黑褐色，有长毛；腺体同雄花。蒴果长可达9毫米，有毛或近无毛，开裂后，果瓣向外反卷。', '花期4月中下旬-5月初，果期5月。', 1527238489, 1527238489, 0);
INSERT INTO `plant` VALUES (17, '小麦', '', 'Triticum aestivum', 'https://api.aiplants.cn/resource/1/%E6%99%AE%E9%80%9A%E5%B0%8F%E9%BA%A6/1319590.jpg#https://api.aiplants.cn/resource/1/%E6%99%AE%E9%80%9A%E5%B0%8F%E9%BA%A6/1540010.jpg#https://api.aiplants.cn/resource/1/%E6%99%AE%E9%80%9A%E5%B0%8F%E9%BA%A6/2099258.jpg#https://api.aiplants.cn/resource/1/%E6%99%AE%E9%80%9A%E5%B0%8F%E9%BA%A6/2099268.jpg', '禾本科', '小麦属', '小麦系植物的统称，是单子叶植物，是一种在世界各地广泛种植的禾本科植物，小麦的颖果是人类的主食之一。小麦是三大谷物之一，中国是世界最早种植小麦的国家之一。', '田家少闲月，五月人倍忙。夜来南风起，小麦覆陇黄。\n妇姑荷箪食，童稚携壶浆，相随饷田去，丁壮在南冈。\n足蒸暑土气，背灼炎天光，力尽不知热，但惜夏日长。\n复有贫妇人，抱子在其旁，右手秉遗穗，左臂悬敝筐。\n听其相顾言，闻者为悲伤。家田输税尽，拾此充饥肠。\n今我何功德，曾不事农桑。吏禄三百石，岁晏有余粮。\n念此私自愧，尽日不能忘。\n--《观刈麦》朝代：唐 作者：白居易', '主治：治心神不宁，失眠，妇女脏躁，烦躁不安，精神抑郁，悲伤欲哭。浮小麦：治自汗，盗汗，骨蒸劳热。小麦富含淀粉、蛋白质、脂肪、矿物质、钙、铁、硫胺素、核黄素、烟酸及维生素A等。', '遍布全国。', '土层深厚，结构良好耕层较深，有利于蓄水保肥，促进根系发育。土壤结构是指固体(有机体和无机体)、液体、气体的组成比例，它与土壤水分、空气，温度、养分有着密切关系。\n\n', '淀粉粒主为扁平的圆形、椭圆形或圆三角状，直径30～40μm，侧面观呈双透镜状、贝壳状，宽11～19μm，两端稍尖或钝圆，脐点裂缝状，复粒少数，由2～4或多分粒组成。横细胞成片，细长柱形，长38～232μm，直径6～21μm，壁含珠状增厚。果皮表皮细胞类长方形或长多角形，长64～216μm，直径16～42μm，壁念珠状增厚。果皮中层细胞细长条形或不规则形，壁念珠状增厚。非腺毛单细胞，长43～950μm，直径11～29μm，壁厚5～11μm。秆直立， 丛生，具6-7节，高60-100厘米，径5-7毫米。叶鞘松弛包茎，下部者长于上部者短于节间；叶舌膜质，长约1毫米；叶片长披针形。穗状花序直立，长5-10厘米（芒除外），宽1-1.5厘米；小穗含3-9小花，上部者不发育；颖卵圆形，长6-8毫米，主脉于背面上部具脊，于顶端延伸为长约1毫米的齿，侧脉的背脊及顶齿均不明显；外稃长圆状披针形，长8-10毫米，顶端具芒或无芒；内稃与外稃几等长。', '冬小麦一般5月上旬至中旬开花,春小麦一般6月中下旬开花。', 1527257448, 1527258278, 0);
INSERT INTO `plant` VALUES (18, '甜瓜', '香瓜、哈密瓜、白兰瓜', 'Cucumis melo', 'https://api.aiplants.cn/resource/1/%E7%94%9C%E7%93%9C/1083417.jpg#https://api.aiplants.cn/resource/1/%E7%94%9C%E7%93%9C/1492427.jpg#https://api.aiplants.cn/resource/1/%E7%94%9C%E7%93%9C/1799598.jpg#https://api.aiplants.cn/resource/1/%E7%94%9C%E7%93%9C/1799618.jpg', '葫芦科', '甜瓜属', '为盛夏的重要水果。含有苹果酸、葡萄糖、氨基酸、甜菜茄、维生素C等丰富营养。', '不惜浑崙次第佥，把将来语太无厌。\n而今一片落谁手，管取甜时彻蒂甜。\n--《甜瓜》朝代：宋 作者：释心月', '全草药用，有祛炎败毒、催吐、除湿、退黄疸等功效。', '全国各地广泛栽培。', '对土壤要求不严格，但以土层深厚，通透性好，不易积。', '一年生匍匐或攀援草本；茎、枝有棱，有黄褐色或白色的糙硬毛和疣状突起。卷须纤细，单一，被微柔毛。叶柄长8-12厘米，具槽沟及短刚毛；叶片厚纸质，近圆形或肾形，长、宽均8-15厘米，上面粗糙，被白色糙硬毛，背面沿脉密被糙硬毛，边缘不分裂或3-7浅裂，裂片先端圆钝，有锯齿，基部截形或具半圆形的弯缺，具掌状脉。花单性，雌雄同株。雄花：数朵簇生于叶腋；花梗纤细，长0.5-2厘米，被柔毛；花萼筒狭钟形，密被白色长柔毛，长6-8毫米，裂片近钻形，直立或开展，比筒部短；花冠黄色，长2厘米，裂片卵状长圆形，急尖；雄蕊3，花丝极短，药室折曲，药隔顶端引长；退化雌蕊长约1毫米。雌花：单生，花梗粗糙，被柔毛；子房长椭圆形，密被长柔毛和长糙硬毛，花柱长1-2毫米，柱头靠合，长约2毫米。果实的形状、颜色因品种而异，通常为球形或长椭圆形，果皮平滑，有纵沟纹，或斑纹，无刺状突起，果肉白色、黄色或绿色，有香甜味；种子污白色或黄白色，卵形或长圆形，先端尖，基部钝，表面光滑，无边缘。', '花果期夏季。', 1527258441, 1527258696, 0);
INSERT INTO `plant` VALUES (19, '杧果', '密望、望果', 'Mangifera indica', 'https://api.aiplants.cn/resource/1/%E6%9D%A7%E6%9E%9C/1000265.jpg#https://api.aiplants.cn/resource/1/%E6%9D%A7%E6%9E%9C/1226366.jpg#https://api.aiplants.cn/resource/1/%E6%9D%A7%E6%9E%9C/1824324.jpg#https://api.aiplants.cn/resource/1/%E6%9D%A7%E6%9E%9C/2016845.jpg', '漆树科', '杧果属', '芒果是杧果的通俗名，是一种原产于印度的漆树科常绿大乔木，其核果大，压扁，成熟时呈黄色，味甜，果核坚硬，是一种非常美味的热带水果。', '', '杧果为热带著名水果，汁多味美，还可制罐头和果酱或盐渍供调味，亦可酿酒。果皮入药，为利尿峻下剂，“凡渡海者食之不呕浪”；果核疏风止咳。叶和树皮可作黄色染料。木材坚硬，耐海水，宜作舟车或家具等。树冠球形，常绿，郁闭度大，为热带良好的庭园和行道树种。', '产云南、广西、广东、福建、台湾。', '芒果性喜温暖，不耐寒霜。温度不足，授粉受精不良，甚至花序枯死或种胚败育死亡。芒果为喜光果树，充足的光照可促进花芽分化、开花坐果和提高果实品质，改善外观。芒果在年雨量700-2000毫米的地区生长良好。', '常绿大乔木，高10-20米；树皮灰褐色，小枝褐色，无毛。叶薄革质，常集生枝顶，叶形和大小变化较大，通常为长圆形或长圆状披针形，长12-30厘米，宽3.5-6.5厘米，先端渐尖、长渐尖或急尖，基部楔形或近圆形，边缘皱波状，无毛，叶面略具光泽，侧脉20-25对，斜升，两面突起，网脉不显，叶柄长2-6厘米，上面具槽，基部膨大。圆锥花序长20-35厘米，多花密集，被灰黄色微柔毛，分枝开展，最基部分枝长6-15厘米；苞片披针形，长约1.5毫米，被微柔毛；花小，杂性，黄色或淡黄色；花梗长1.5-3毫米，具节；萼片卵状披针形，长2.5-3毫米，宽约1.5毫米，渐尖，外面被微柔毛，边缘具细睫毛；花瓣长圆形或长圆状披针形，长3.5-4毫米，宽约1.5毫米，无毛，里面具3-5条棕褐色突起的脉纹，开花时外卷；花盘膨大，肉质，5浅裂；雄蕊仅1个发育，长约2.5毫米，花药卵圆形，不育雄蕊3-4，具极短的花丝和疣状花药原基或缺；子房斜卵形，径约1.5毫米，无毛，花柱近顶生，长约2.5毫米。核果大，肾形（栽培品种其形状和大小变化极大），压扁，长5-10厘米，宽3-4.5厘米，成熟时黄色，中果皮肉质，肥厚，鲜黄色，味甜，果核坚硬。', '芒果树自然开花在每年12月至次年1-2月，有时会早至11月或迟到次年3月，盛花期在春节前后。', 1527258443, 1527258693, 0);
INSERT INTO `plant` VALUES (20, '长裂苦苣菜', '', 'Sonchus brachyotus', 'https://api.aiplants.cn/resource/1/%E9%95%BF%E8%A3%82%E8%8B%A6%E8%8B%A3%E8%8F%9C/1085231.jpg#https://api.aiplants.cn/resource/1/%E9%95%BF%E8%A3%82%E8%8B%A6%E8%8B%A3%E8%8F%9C/1440646.jpg#https://api.aiplants.cn/resource/1/%E9%95%BF%E8%A3%82%E8%8B%A6%E8%8B%A3%E8%8F%9C/15022.jpg#https://api.aiplants.cn/resource/1/%E9%95%BF%E8%A3%82%E8%8B%A6%E8%8B%A3%E8%8F%9C/1802217.jpg', '菊科', '苦苣菜属', '一年生草本，菊科菊苣属中以嫩叶为食的栽培种。', '', '具有清热解毒、凉血利湿、消肿排脓、祛瘀止痛、补虚止咳的功效。促进生长发育和消暑保健有较好的作用。苦菜水煎浓缩乙醇提取液，对急性淋巴细胞性白血病﹑急性及慢性粒细胞白血病都有抑制作用。', '分布黑龙江（具体地点不详）、吉林（安图、通余）、内蒙古（海拉尔、包头）、河北（内丘、石家庄、张家口）、山西（交城、榆次、宁武、垣曲）、陕西（商县、榆林、延安）、山东（济南）。', '生于山地草坡、河边或碱地，海拔350-2260米', '一年生草本，高50-100厘米。根垂直直伸，生多数须根。茎直立，有纵条纹，基部直径达1.2毫米，上部有伞房状花序分枝，分枝长或短或极短，全部茎枝光滑无毛。基生叶与下部茎叶全形卵形、长椭圆形或倒披针形，长6-19厘米，宽1.5-11厘米，羽状深裂、半裂或浅裂，极少不裂，向下渐狭，无柄或有长1-2厘米的短翼柄，基部圆耳状扩大，半抱茎，侧裂片3-5对或奇数，对生或部分互生或偏斜互生，线状长椭圆形、长三角形或三角形，极少半圆形，顶裂片披针形，全部裂片边缘全缘，有缘毛或无缘毛或缘毛状微.齿，顶端急尖或钝或圆形；中上部茎叶与基生叶和下部茎叶同形并等样分裂，但较小；最上部茎叶宽线形或宽线状披针形，接花序下部的叶常钻形；全部叶两面光滑无毛。头状花序少数在茎枝顶端排成伞房状花序。总苞钟状，长1.5-2厘米，宽1-1.5厘米；总苞片4-5层，最外层卵形，长6毫米，宽3毫米，中层长三角形至披针形，长9-13毫米，宽2-5-3毫米，内层长披针形，长1.5厘米，宽2毫米，全部总苞片顶端急尖，外面光滑无毛。舌状小花多数，黄色。瘦果长椭圆状，褐色，稍压扁，长约3毫米，宽约1. 5毫米，每面有5条高起的纵肋，肋间有横皱纹。冠毛白色，纤细，柔软，纠缠，单毛状，长1.2厘米。', '花果期6-9月。', 1527258463, 1527258463, 0);
INSERT INTO `plant` VALUES (21, '香石竹', '狮头石竹、康乃馨、麝香石竹、大花石竹', 'Dianthus caryophyllus', 'https://api.aiplants.cn/resource/1/%E9%A6%99%E7%9F%B3%E7%AB%B9/1043336.jpg#https://api.aiplants.cn/resource/1/%E9%A6%99%E7%9F%B3%E7%AB%B9/1247042.jpg#https://api.aiplants.cn/resource/1/%E9%A6%99%E7%9F%B3%E7%AB%B9/1861906.jpg#https://api.aiplants.cn/resource/1/%E9%A6%99%E7%9F%B3%E7%AB%B9/1861908.jpg', '石竹科', '石竹属', '香石竹因其花色丰富、花型多变而成为世界著名四大切花之一。', '一自幽山别，相逢此寺中。\n高低俱出叶，深浅不分丛。\n野蝶难争白，庭榴暗让红。\n谁怜芳最久，春露到秋风。\n--《云阳寺石竹花》年代：唐  作者：司空曙 ', '香石竹为最重要的切花之一，尤系冬季重要切花。也常盆栽观赏。此外，花朵还可提取香精用。[3]  香石竹乃国际市场上切花用量最大的种类之一，也可作为布置花坛的材料。其花还可提取香精作化妆品材料。香石竹有很多园艺品种，耐瓶插，常用作切花，温室培养可四季开花。\n\n', '我国广泛栽培。', '喜冷凉气候，但不耐寒，喜空气流通、干燥及日光充足之环境。要求排水良好、腐殖质丰富、保肥性能良好而微呈碱性之粘质土壤，忌连作及低洼地。喜肥。能耐一定低温，在上海地区可露地越冬，但不能开花；为了冬季供应切花，常须带土球移入室内作床栽培。性好凉爽而不耐炎热，但因品种而有不同的适温：黄色系20-25℃时生长最好，10-20℃时开花最好；红色系适温要求高于25℃，否则生长缓慢甚至不开花。', '多年生草本，高40-70厘米，全株无毛，粉绿色。茎丛生，直立，基部木质化，上部稀疏分枝。叶片线状披针形，长4-14厘米，宽2-4毫米，顶端长渐尖，基部稍成短鞘，中脉明显，上面下凹，下面稍凸起。花常单生枝端，有时2或3朵，有香气，粉红、紫红或白色；花梗短于花萼；苞片4 (-6)，宽卵形，顶端短凸尖，长达花萼1/4；花萼圆筒形，长2.5-3厘米，萼齿披针形，边缘膜质；瓣片倒卵形，顶缘具不整齐齿；雄蕊长达喉部；花柱伸出花外。蒴果卵球形，稍短于宿存萼。', '花期5-8月。', 1527258465, 1527258465, 0);
INSERT INTO `plant` VALUES (22, '甘蔗', '秀贵甘蔗', 'Saccharum officinarum', 'https://api.aiplants.cn/resource/1/%E7%94%98%E8%94%97/1431349.jpg#https://api.aiplants.cn/resource/1/%E7%94%98%E8%94%97/1431350.jpg#https://api.aiplants.cn/resource/1/%E7%94%98%E8%94%97/1431351.jpg#https://api.aiplants.cn/resource/1/%E7%94%98%E8%94%97/725037.jpg', '禾本科', '甘蔗属', '多年生高大实心草本。根状茎粗壮发达。是我国是世界上三大甘蔗生产国（巴西、印度和中国）之一。', '', '茎秆为重要的制糖原料，且可提炼乙醇作为能源替代品；纤维素供制纸；作家畜饲料及供药用；制酒精及建筑材料等。', '广东、台湾、广西、福建、四川、云南、江西、贵州、湖南、浙江、湖北、海南等南方12个省、自治区。', '喜温、喜光作物，年积温需5500℃-8500℃，无霜期330d以上，年均空气湿度60%，年降水量要求800-1200mm，日照时数在1195小时以上。', '多年生高大实心草本。根状茎粗壮发达。秆高3-5（-6）米。直径2-4（-5）厘米，具20-40节，下部节间较短而粗大，被白粉。叶鞘长于其节间，除鞘口具柔毛外余无毛；叶舌极短，生纤毛，叶片长达1米，宽4-6厘米，无毛，中脉粗壮，白色，边缘具锯齿状粗糙。圆锥花序大型，长50厘米左右，主轴除节具毛外余无毛，在花序以下部分不具丝状柔毛；总状花序多数轮生，稠密；总状花序轴节间与小穗柄无毛；小穗线状长圆形，长3.5-4毫米；基盘具长于小穗2-3倍的丝状柔毛；第一颖脊间无脉，不具柔毛，顶端尖，边缘膜质；第二颖具3脉，中脉成脊，粗糙，无毛或具纤毛；第一外稃膜质，与颖近等长，无毛；第二外稃微小，无芒或退化；第二内稃披针形；鳞被无毛。', '', 1527258816, 1527258833, 0);
INSERT INTO `plant` VALUES (23, '草麻黄', '麻黄、华麻黄', 'Ephedra sinica', 'https://api.aiplants.cn/resource/1/%E8%8D%89%E9%BA%BB%E9%BB%84/1645827.jpg#https://api.aiplants.cn/resource/1/%E8%8D%89%E9%BA%BB%E9%BB%84/572563.jpg#https://api.aiplants.cn/resource/1/%E8%8D%89%E9%BA%BB%E9%BB%84/572564.jpg#https://api.aiplants.cn/resource/1/%E8%8D%89%E9%BA%BB%E9%BB%84/572567.jpg', '麻黄科', '麻黄属', '草本状灌木，为重要的药用植物。', '', '生物碱含量丰富，为中国提制麻黄碱的主要植物。', '辽宁、吉林、内蒙古、河北、山西、河南西北部及陕西等省区。', '适应性强，习见于山坡、平原、干燥荒地、河床及草原等处，常组成大面积的单纯群落。', '草本状灌木，高20-40厘米；木质茎短或成匍匐状，小枝直伸或微曲，表面细纵槽纹常不明显，节间长2.5-5.5厘米，多为3-4厘米，径约2毫米。叶2裂，鞘占全长1/3-2/3，裂片锐三角形，先端急尖。雄球花多成复穗状，常具总梗，苞片通常4对，雄蕊7-8，花丝合生，稀先端稍分离；雌球花单生，在幼枝上顶生，在老枝上腋生，常在成熟过程中基部有梗抽出，使雌球花呈侧枝顶生状，卵圆形或矩圆状卵圆形，苞片4对，下部3对合生部分占1/4-1/3,最上一对合生部分达1/2以上；雌花2,胚珠的珠被管长l毫米或稍长，直立或先端微弯，管口隙裂窄长，约占全长的1/4-1/2,裂口边缘不整齐，常被少数毛茸。雌球花成熟时肉质红色，矩圆状卵圆形或近于圆球形，长约8毫米，径6-7毫米；种子通常2粒，包于苞片内，不露出或与苞片等长，黑红色或、灰褐色，三角状卵圆形或宽卵圆形，长5-6毫米，径2.5-3.5毫米，表面具细皱纹，种脐明显，半圆形。\n', '花期5-6月，种子8-9月成熟。', 1527322578, 1527322578, 0);
INSERT INTO `plant` VALUES (24, '玉兰', '木兰、玉堂春、迎春花', 'Yulania denudata', 'https://api.aiplants.cn/resource/1/%E7%8E%89%E5%85%B0/1002700.jpg#https://api.aiplants.cn/resource/1/%E7%8E%89%E5%85%B0/1002709.jpg#https://api.aiplants.cn/resource/1/%E7%8E%89%E5%85%B0/137094.jpg#https://api.aiplants.cn/resource/1/%E7%8E%89%E5%85%B0/138265.jpg', '木兰科', '玉兰属', '落叶乔木。 原产于中国中部各省，现北京及黄河流域以南均有栽培。花白色到淡紫红色，大型、芳香，花冠杯状，花先开放，叶子后长，花期10天左右。中国著名的花木，南方早春重要的观花树木。上海市市花。', '萱草堂前锦棣花。灵椿树下玉兰芽。二毛鬓莫惊青鉴，五朵云须上白麻。\n携斗酒，醉君家。春风吹我帽帘斜。座中贵客应相笑，前日疏狂未减些。\n--《鹧鸪天 寿潘君美》年代: 元 作者: 刘敏中', '玉兰花含有丰富的维生素、氨基酸和多种微量元素，有祛风散寒，通气理肺之效。可加工制作小吃。材质优良，纹理直，结构细，供家具、图板、细木工等用；花蕾入药与“辛夷”功效向；花含芳香油，可提取配制香精或制浸膏；花被片食用或用以熏茶；种子榨油供工业用。为驰名中外的庭园观赏树种。', '产于江西（庐山）、浙江（天目山）、河南（伏牛山）、湖南（衡山）、贵州。生于海拔 500-1 000米的林中。现中国各大城市园林广泛栽培，其中河南省南召县是中国林学会授予的“中国玉兰之乡”。', '喜光，幼树较耐阴，不耐强光和西晒，光照过强或西晒，容易使树木受到灼伤。玉兰不耐移植，一般在萌芽前10至15天或花刚谢而未展叶时移栽较为理想。种植完毕后，应立即浇水，3天后浇二水，5天后浇三水，三水后可进入正常管理。如果所种苗木带有花蕾，应将花蕾剪除，防止开花结果消耗大量养分而影响成活率。玉兰喜肥，除在栽植时施用基肥外，此后每年都应施肥。', '落叶乔木；树皮深灰色，粗糙开裂；小枝稍粗壮，灰褐色；叶纸质，倒卵形、宽倒卵形或、倒卵状椭圆形，基部徒长枝叶椭圆形玉兰争艳，叶上深绿色，嫩时被柔毛，后仅中脉及侧脉留有柔毛，下面淡绿色，沿脉上被柔毛，托叶痕为叶柄长的1/4-1/3。花蕾卵圆形，花先叶开放，直立，芳香，花被片9片，白色，基部常带粉红色，近相似，长圆状倒卵形。蓇葖厚木质，褐色，具白色皮孔；种子心形。', '花期2-3月（亦常于7-9月再开一次花），果期8-9月。', 1527322599, 1527322599, 0);
INSERT INTO `plant` VALUES (25, '绣球荚蒾', '', 'Viburnum macrocephalum', 'https://api.aiplants.cn/resource/1/%E7%BB%A3%E7%90%83%E8%8D%9A%E8%92%BE/1258690.jpg#https://api.aiplants.cn/resource/1/%E7%BB%A3%E7%90%83%E8%8D%9A%E8%92%BE/1317510.jpg#https://api.aiplants.cn/resource/1/%E7%BB%A3%E7%90%83%E8%8D%9A%E8%92%BE/1500947.jpg#https://api.aiplants.cn/resource/1/%E7%BB%A3%E7%90%83%E8%8D%9A%E8%92%BE/1689083.jpg', '五福花科', '荚蒾属', ' 落叶或半常绿灌木。花生于第三级辐射枝上；花冠白色，辐状，裂片圆状倒卵形，花药近圆形。花序为大型白色花朵，形状像绣球，非常美观，是优良观花园林绿化树种。', '', '用于城市和庭院的绿化树种，栽于园路旁边，使其拱形花枝形成花廊；植于庭中堂前，墙下窗前，也极适宜，还可作大型花坛的中心树。', '江苏、浙江、江西和河北等省均见有栽培。', '喜光，略耐阴，喜温暖湿润气候，较耐寒，宜在肥沃、湿润、排水良好的土壤中生长。', '高可达4米；树皮灰褐色或灰白色；芽、幼技、叶柄均密被灰白色或黄白色簇状短毛，后渐变无毛。叶纸质，卵形至椭圆形或卵状矩圆形，边缘有小齿，侧近缘前互相网结，聚伞花序，全部由大型不孕花组成，花生于第三级辐射枝上；萼筒筒状，萼齿与萼筒几等长，花冠白色，辐状，裂片圆状倒卵形，花药近圆形。', '4-5月开花。', 1527322609, 1527322609, 0);
INSERT INTO `plant` VALUES (26, '麝香百合', '', 'Lilium longiflorum', 'https://api.aiplants.cn/resource/1/%E9%BA%9D%E9%A6%99%E7%99%BE%E5%90%88/1107221.jpg#https://api.aiplants.cn/resource/1/%E9%BA%9D%E9%A6%99%E7%99%BE%E5%90%88/1244304.jpg#https://api.aiplants.cn/resource/1/%E9%BA%9D%E9%A6%99%E7%99%BE%E5%90%88/1970870.jpg#https://api.aiplants.cn/resource/1/%E9%BA%9D%E9%A6%99%E7%99%BE%E5%90%88/260363.jpg', '百合科', '百合属', '鳞茎球形或近球形，高2.5-5厘米；鳞片白色。是一种美丽的花卉，花极香，含有芳香油，可作香料。', '', '麟茎及花：清热解毒，润肺止咳。用于咳嗽，尿血，胎盘不下，无名肿毒。食用及作调料均可。\n鳞叶味甘微苦,具有润肺止咳、清心安神的功效;用于肺痨久咳、咳嗽痰血、虚烦惊悸、精神恍惚等。花具有润肺清火、安神的功效;用于咳嗽、眩晕、夜寐不安、天疮湿疮等。', '产我国台湾。', '要求腐殖质丰富、排水良好的微酸性土壤，在石灰质及偏碱性土壤中生长不良。它性喜夏季凉爽湿润气候。5℃时，植株生长处于停滞状态；10℃以上，植株才能正常生长。', '鳞茎球形或近球形，高2.5-5厘米；鳞片白色。茎高45-90厘米，绿色，基部为淡红色。叶散生，披针形或矩圆状披针形，长8-15厘米，宽1-1.8厘米，先端渐尖，全缘，两面无毛。花单生或2-3朵；花梗长3厘米；苞片披针形至卵状披针形，长约8厘米，宽1-1.4厘米；花喇叭形，白色，筒外略带绿色，长达19厘米；外轮花被片上端宽2.5-4厘米；内轮花被片较外轮稍宽，蜜腺两边无乳头状突起。花丝长15厘米，无毛；子房圆柱形，长4厘米，柱头3裂。蒴果矩圆形，长5-7厘米。\n为一美丽的花卉，花极香，含有芳香油，可作香料。', '花期6-7月，果期8-9月。\n\n\n', 1527322869, 1527322869, 0);
INSERT INTO `plant` VALUES (27, '粉叶小檗', '', 'Berberis pruinosa', 'https://api.aiplants.cn/resource/1/%E7%B2%89%E5%8F%B6%E5%B0%8F%E6%AA%97/1797137.jpg#https://api.aiplants.cn/resource/1/%E7%B2%89%E5%8F%B6%E5%B0%8F%E6%AA%97/2567765.jpg#https://api.aiplants.cn/resource/1/%E7%B2%89%E5%8F%B6%E5%B0%8F%E6%AA%97/2592677.jpg#https://api.aiplants.cn/resource/1/%E7%B2%89%E5%8F%B6%E5%B0%8F%E6%AA%97/2595504.jpg', '小檗科', '小檗属', '常绿灌木，生于灌丛中，高山栎林、云杉林缘、路边或针叶林下。海拔1800-4000米。', '', '根富含小檗碱，多用为提取原料，供药用，具有清热解毒、消炎止痢的功效。', '四川、贵州、云南、西藏', '', '高1-2米。枝圆柱形，棕灰色或棕黄色，被黑色疣点；茎刺粗壮，三分叉，长2-3.5厘米，腹面具槽或扁平，与枝同色。叶硬革质，椭圆形，倒卵形，少有椭圆状披针形，长2-6厘米，宽1-2.5厘米，先端钝尖或短渐尖，基部楔形，上面亮黄绿色或灰绿色，中脉扁平，侧脉微隆起，背面被白粉或无白粉，中脉明显隆起，侧脉微显，两面网脉不显，叶缘微向背面反卷或平展，通常具1-6刺锯齿或刺齿，偶有全缘或多达8-9刺齿；近无柄。花（8-）10-20朵簇生；花梗长10-20毫米，纤细；小苞片披针形，长约2毫米，先端渐尖；萼片2轮，外萼片长圆状椭圆形，长约4毫米，宽约2毫米，先端钝圆，内萼片倒卵形，长约6.5毫米，宽约5毫米，先端圆形；花瓣倒卵形，长约7毫米，宽4-5毫米，先端深缺裂，基部缢缩呈爪，具2枚分离腺体；雄蕊长约6毫米，药隔先端近平截；胚珠2-3枚。浆果椭圆形或近球形，长6-7毫米，直径4-5毫米，顶端通常无宿存花柱，有时具短宿存花柱，密被或微被白粉；含种子2枚。', '花期3-4月，果期6-8月。', 1527323611, 1527323611, 0);
INSERT INTO `plant` VALUES (28, '木姜子', '木香子、山胡椒、香桂子', 'Litsea pungens', 'https://api.aiplants.cn/resource/1/%E6%9C%A8%E5%A7%9C%E5%AD%90/1323587.jpg#https://api.aiplants.cn/resource/1/%E6%9C%A8%E5%A7%9C%E5%AD%90/1428163.jpg#https://api.aiplants.cn/resource/1/%E6%9C%A8%E5%A7%9C%E5%AD%90/1669118.jpg#https://api.aiplants.cn/resource/1/%E6%9C%A8%E5%A7%9C%E5%AD%90/1669120.jpg', '樟科', '木姜子属', '一味草药，果实圆球形。表面棕褐色至棕黑色有网状皱纹，基部常有果柄痕。中果皮易剥去；内果皮暗棕红色，果皮坚脆，种 子1粒，内有肥厚子叶2枚， 富含油质。', '', '主治温中行气止痛；燥湿健脾消食；解毒消肿。主胃寒腹痛；暑湿吐泻；食滞饱胀；痛经；疝痛；疟疾；疮疡肿痛等症状。', '湖北、湖南、广东北部、广西、四川、贵州、云南、西藏、甘肃、陕西、河南、山西南部、浙江南部。', '生于溪旁和山地阳坡杂木林中或林缘，海拔800-2300米。', '落叶小乔木，高3-10米；树皮灰白色。幼枝黄绿色，被柔毛，老枝黑褐色，无毛。顶芽圆锥形，鳞片无毛。叶互生，常聚生于枝顶，披针形或倒卵状披针形，长4-15厘米，宽2-5.5厘米，先端短尖，基部楔形，膜质，幼叶下面具绢状柔毛，后脱落渐变无毛或沿中脉有稀疏毛，羽状脉，侧脉每边5-7条，叶脉在两面均突起；叶柄纤细，长1-2厘米，初时有柔毛，后脱落渐变无毛。伞形花序腋生；总花梗长5-8毫米，无毛；每一花序有雄花8-12朵，先叶开放；花梗长5-6毫米，被丝状柔毛；花被裂片6，黄色，倒卵形，长2.5毫米，外面有稀疏柔毛；能育雄蕊9，花丝仅基部有柔毛，第3轮基部有黄色腺体，圆形；退化雌蕊细小，无毛。果球形，直径7-10毫米，成熟时蓝黑色；果梗长1-2.5厘米，先端略增粗。', '花期3-5月，果期7-9月。', 1527325384, 1527422941, 0);
INSERT INTO `plant` VALUES (29, '普陀南星', '', 'Arisaema ringens', 'https://api.aiplants.cn/resource/1/%E6%99%AE%E9%99%80%E5%8D%97%E6%98%9F/2578208.jpg#https://api.aiplants.cn/resource/1/%E6%99%AE%E9%99%80%E5%8D%97%E6%98%9F/2614672.jpg#https://api.aiplants.cn/resource/1/%E6%99%AE%E9%99%80%E5%8D%97%E6%98%9F/2631062.jpg#https://api.aiplants.cn/resource/1/%E6%99%AE%E9%99%80%E5%8D%97%E6%98%9F/2642132.jpg', '天南星科', '天南星属', '块茎扁球形，具小球茎。鳞片向上渐狭，内面的长约12厘米。块茎(辛、苦，温。有毒。用于毒肿热结。', '', '块茎(由跋)：辛、苦，温。有毒。用于毒肿热结。\n燥湿化痰；祛风定惊；消肿散结。主痰湿咳嗽；风痰眩晕；中风；口眼喎斜；破伤风；痈疮肿毒。', '产江苏、浙江、台湾。', '沟边山坡林下阴湿地。', '块茎扁球形，具小球茎。鳞片向上渐狭，内面的长约12厘米。叶2（-1），叶柄长15-30厘米，粗7-8毫米，下部1/3具鞘，鞘管状，口部截形；叶片3全裂，裂片无柄或具短柄，中裂片宽椭圆形，长16-23厘米；侧裂片偏斜，长圆形或椭圆形，长15-18厘米，宽均在10厘米以上，先端渐尖，具长1-1.5厘米的锥状突尖，侧脉脉距约1厘米，集合脉距边缘5毫米。花序柄短于叶柄，有时长为叶柄的1/2。佛焰苞管部绿色，宽倒圆锥形，长3.6-4厘米，上部粗1.8-2厘米，喉部多少具宽耳，耳内面深紫，外卷；檐部下弯成盔状，前檐具卵形唇片，下垂，先端外弯。肉穗花序单性，雄花序无柄，圆柱形，长1.5厘米，粗8毫米，雄花规则地螺旋状排列，每花花药2，具短柄，2室，药室短卵圆形，顶孔横裂；雌花序近球形，长宽1.5厘米，子房卵圆形，顶部渐狭，胚珠1；各附属器棒状或长圆锥状，长3.5-4.5厘米，先端钝，基部增粗至0.9-1.2厘米，具长5-10毫米的柄，向上渐狭，上部粗5-8毫米。', '花期4月。\n\n', 1527331012, 1527423733, 0);
INSERT INTO `plant` VALUES (30, '水竹', '', 'Phyllostachys heteroclada', 'https://api.aiplants.cn/resource/1/%E6%B0%B4%E7%AB%B9/1283362.jpg#https://api.aiplants.cn/resource/1/%E6%B0%B4%E7%AB%B9/1689252.jpg#https://api.aiplants.cn/resource/1/%E6%B0%B4%E7%AB%B9/1689255.jpg#https://api.aiplants.cn/resource/1/%E6%B0%B4%E7%AB%B9/1788322.jpg', '禾本科', '刚竹属', '水竹禾本科、刚竹属多年生草本，竿可高6米许，粗达3厘米，幼竿具白粉并疏生短柔毛；节间长达30厘米，壁厚3-5毫米；竿环在较粗的竿中较平坦，与箨环同高，在较细的竿中则明显隆起而高于箨环。', '水竹能潇洒，天然二尺强。\n低枝均雨露，直节惯风霜。\n不拟编书简，何时宿凤凰？\n幽人颇相得，拂拭近茅堂。\n--《水竹》年代：元  作者：王冕 ', '水竹竹材韧性好，栽培的水竹竹竿粗直，节较平，宜编制各种生活及生产用具。著名的湖南益阳水竹席就是用本种为材料编制而成的；笋供食用。', '产黄河流域及其以南各地。', '水竹性喜温暖湿润和通风透光，耐荫，忌烈日曝晒。光照充足的环境，耐半阴，不耐寒，华东地区露地稍加保护可以越冬，对土壤要求不严，以肥沃稍粘的土质为宜。生长在河岸、湖旁灌丛中或岩石山坡。水竹喜温暖湿润和腐殖质丰富的粘性土壤，耐阴不耐寒，冬季温度不低于5℃。', '竿可高6米许，粗达3厘米，幼竿具白粉并疏生短柔毛；节间长达30厘米，壁厚3-5毫米；竿环在较粗的竿中较平坦，与箨环同高，在较细的竿中则明显隆起而高于箨环；节内长约5毫米；分枝角度大，以致接近于水平开展。箨鞘背面深绿带紫色（在细小的笋上则为绿色），无斑点，被白粉，无毛或疏生短毛，边缘生白色或淡褐色纤毛；箨耳小，但明显可见，淡紫色，卵形或长椭圆形，有时呈短镰形，边缘有数条紫色繸毛，在小的箨鞘上则可无箨耳及鞘口繸毛或仅有数条细弱的繸毛；箨舌低，微凹乃至微呈拱形，边缘生白色短纤毛；箨片直立，三角形至狭长三角形，绿色，绿紫色或紫色，背部呈舟形隆起。末级小枝具2叶，稀可1或3叶；叶鞘除边缘外无毛；无叶耳，鞘口繸毛直立，易断落；叶舌短；叶片披针形或线状披针形，长5.5-12.5厘米，宽1-1.7厘米，下表面在基部有毛。花枝呈紧密的头状，长(16)18-20(22)毫米，通常侧生于老枝上，基部托以4-6片逐渐增大的鳞片状苞片，如生于具叶嫩枝的顶端，则仅托以1或2片佛焰苞，后者的顶端有卵形或长卵形的叶状缩小叶，如在老枝上的花枝则具佛焰苞2-6片，纸质或薄革质，广卵形或更宽，惟渐向顶端者则渐狭窄；并变为草质，长约9-12毫米，先端具短柔毛，边缘生纤毛，其他部分无毛或近于无毛，顶端具小尖头，每片佛焰苞腋内有假小穗4-7枚，有时可少至1枚；假小穗下方常托以形状、大小不一的苞片，此苞片长达12毫米，多少呈膜质，背部具脊，先端渐尖，先端及脊上均具长柔毛，侧脉2或3对，极细弱。小穗长达15毫米，含3-7朵小花，上部小花不孕；小穗轴节间长1.5-2毫米，棒状，无毛，顶端近于截形；颖0-3片，大小、形状、质地与其下的苞片相同，有时上部者则可与外稃相似；外稃披针形，长8-12毫米，上部或中上部被以斜开展的柔毛，9-13脉，背脊仅在上端可见，先端锥状渐尖；内稃多少短于外稃，除基部外均被短柔毛；鳞被菱状卵形，长约3毫米，有7条细脉纹，边缘生纤毛；花药长5-6毫米；花柱长约5毫米，柱头3，有时2，羽毛状。果实未见。笋期5月。', '花期4-8月。', 1527331026, 1527343885, 0);
INSERT INTO `plant` VALUES (31, '黄金间碧竹', '', 'Bambusa vulgaris cv. Vittata', 'https://api.aiplants.cn/resource/1/%E9%BB%84%E9%87%91%E9%97%B4%E7%A2%A7%E7%AB%B9/1017515.jpg#https://api.aiplants.cn/resource/1/%E9%BB%84%E9%87%91%E9%97%B4%E7%A2%A7%E7%AB%B9/1272852.jpg#https://api.aiplants.cn/resource/1/%E9%BB%84%E9%87%91%E9%97%B4%E7%A2%A7%E7%AB%B9/1995644.jpg#https://api.aiplants.cn/resource/1/%E9%BB%84%E9%87%91%E9%97%B4%E7%A2%A7%E7%AB%B9/724411.jpg', '禾本科', '簕竹属', '是龙头竹的一个变种，禾本科植物，竿稍疏离，高8-15米，直径5-9厘米，尾梢下弯，下部挺直或略呈“之”字形曲折，竿黄色，节间正常。', '', '其杆及主枝呈黄色，其节间于分枝一侧之沟槽中常呈鲜绿色，有时其旁侧亦有同样绿色条纹2～3 条，故名黄金间碧竹，是一种既有经济价值、又有观赏价值的名优品种。', '我国广西、海南、云南、广东和台湾等省区的南部地区庭园中有栽培。', '', '原变种（龙头竹）竿稍疏离，高8-15米，直径5-9厘米，尾梢下弯，下部挺直或略呈“之”字形曲折；节间深绿色，长20-30厘米，幼时稍被白蜡粉，并贴生以淡棕色刺毛，老则无粉无毛，竿壁稍厚；节处稍隆起，竿基数节具短气根，并于箨环之上下方各环生一圈灰白色绢毛；分枝常自竿下部节开始，每节数枝至多枝簇生，主枝较粗长。箨鞘早落，背面密生脱落性暗棕色刺毛，干时纵肋稍隆起，先端在与箨片连接处呈拱形，但在与箨耳连接处作弧形下凹；箨耳甚发达，彼此近等大而近同形，长圆形或肾形，斜升，宽8-10毫米，边缘具弯曲细繸毛；箨舌高3-4毫米，边缘细齿裂，并被极短的白色细纤毛；箨片直立或外展，易脱落，宽三角形至三角形，背面疏生暗棕色小刺毛，腹面在脉间密生暗棕色小刺毛，尤以其基部更密，先端的边缘内卷形成坚硬的锐尖头，基部稍作圆形收窄，且其宽度约为箨鞘先端宽的一半，边缘在近基部处具弯曲细繸毛。叶鞘初时疏生棕色糙硬毛，后变无毛；叶耳常不发达，若存时，多为宽镰刀形，边缘无毛或仅具少数繸毛；叶舌高1毫米或更低，截形，全缘；叶片窄被针形，一般长10-30厘米，宽13-25毫米，两表面均无毛，先端渐尖具粗糙钻状尖头，基部近圆形而两侧稍不对称，小横脉在叶下表面略微可见。假小穗以数枚簇生于花枝各节；小穗稍扁，狭披针形至线状披针形，长2-3.5厘米，宽4-5毫米，含小花5-10朵，基部托以数片具芽苞片；小穗轴节间长1.5-3毫米；颖1或2片，背面仅于近顶端被短毛，先端具硬尖头；外稃长8-10毫米，背面近顶端被短毛，先端具硬尖头；内稃略短于其外稃，具2脊，脊上被短纤毛；鳞被3，长2-2.5毫米，边缘被长纤毛；花药长6毫米，顶端具一小簇短毛；花柱细长，长3-7毫米，柱头短，3枚。', '', 1527331041, 1527331084, 0);
INSERT INTO `plant` VALUES (32, '刚竹', '', 'Phyllostachys sulphurea var. viridis', 'https://api.aiplants.cn/resource/1/%E5%88%9A%E7%AB%B9/1366672.jpg#https://api.aiplants.cn/resource/1/%E5%88%9A%E7%AB%B9/1441539.jpg#https://api.aiplants.cn/resource/1/%E5%88%9A%E7%AB%B9/1452151.jpg#https://api.aiplants.cn/resource/1/%E5%88%9A%E7%AB%B9/1452152.jpg', '禾本科', '刚竹属', '属于多年生一次结实植物,毕生开花一次,花后营养体自然死亡。竹子开花由内部计时机制所控制,这种计时机制对环境干扰有强大的缓冲能力。同步开花是刚竹属开花的基本特征。', '潇洒城东楼，绕楼多修竹。\n森然一万竿，白粉封青玉。\n卷帘睡初觉，欹枕看未足。\n影转色入楼，床席生浮绿。\n空城绝宾客，向夕弥幽独。\n楼上夜不归，此君留我宿。\n--《东楼竹》年代: 唐 作者: 白居易', '竿可作小型建筑用材和各种农具柄；笋供食用，惟味微苦。', '原产我国，黄河至长江流域及福建均有分布。', '刚竹抗性强，适应酸性土至中性土，但PH8.5左右的碱性土及含盐0.1%的轻盐土亦能生长，但忌排水不良。能耐-18℃的低温。主要分布在我国长江流域。生于低山坡。竹材强韧。', '竿高6-15米，直径4-10厘米，幼时无毛，微被白粉，绿色，成长的竿呈绿色或黄绿色，在10倍放大镜下可见猪皮状小凹穴或白色晶体状小点；中部节间长20-45厘米，壁厚约5毫米；竿环在较粗大的竿中于不分枝的各节上不明显；箨环微隆起。箨鞘背面呈乳黄色或绿黄褐色又多少带灰色，有绿色脉纹，无毛，微被白粉，有淡褐色或褐色略呈圆形的斑点及斑块；箨耳及鞘口繸毛俱缺；箨舌绿黄色，拱形或截形，边缘生淡绿色或白色纤毛；箨片狭三角形至带状，外翻，微皱曲，绿色，但具桔黄色边缘。末级小枝有2-5叶；叶鞘几无毛或仅上部有细柔毛；叶耳及鞘口繸毛均发达；叶片长圆状披针形或披针形，长5.6-13厘米，宽1.1-2.2厘米。', '花枝未见。笋期5月中旬。', 1527331065, 1527331065, 0);
INSERT INTO `plant` VALUES (33, '麻竹', '甜竹', 'Dendrocalamus latiflorus', 'https://api.aiplants.cn/resource/1/%E9%BA%BB%E7%AB%B9/1085488.jpg#https://api.aiplants.cn/resource/1/%E9%BA%BB%E7%AB%B9/1835288.jpg#https://api.aiplants.cn/resource/1/%E9%BA%BB%E7%AB%B9/1835293.jpg#https://api.aiplants.cn/resource/1/%E9%BA%BB%E7%AB%B9/2489978.jpg', '禾本科', '牡竹属', '麻竹，别名：葫芦麻竹、美浓麻竹，（学名：Dendrocalamus latiflorus Munro），竿高20~25米，直径15~30厘米，梢端长下垂或弧形弯曲；节间长45~60厘米， 幼时被白粉，但无毛，仅在节内具一圈棕色绒毛环；壁厚1~3厘米；竿分枝习性高，每节分多枝，主枝常单一。是中国南方栽培最广的竹种，笋味甜美，每年均有大量笋干和罐头上市，甚至远销日本和欧美等国。竿亦供建筑和篾用，庭园栽植，观赏价值也高。', '独坐幽篁里，弹琴复长啸。\n深林人不知，明月来相照。\n--《竹里馆》年代：唐 作者：王维', '麻竹竹笋味道鲜美，营养丰富。竹竿是很好的建筑材料。除此之外，其竹型优美，叶片幽雅，可以庭院栽种，还是很好的观赏植物。', '四川、贵州、云南、福建、台湾、广东、广西、海南、香港', '麻竹从出笋期到竹材砍伐利用期，可能受到竹象鼻虫、竹螟、竹蚜虫等各种病虫害的危害，因此，病虫害防治是麻竹生产的一项重要的工作。', '秆高20-25米，径15-30厘米，梢端弓形下弯，中部节间长40-60厘米，新杆被白粉，无毛；基部数节节间具黄褐色毛环，箨环常有箨鞘基部的残余物。', '笋期7-9月。', 1527331068, 1527331068, 0);
INSERT INTO `plant` VALUES (34, '黄竹', '', 'Dendrocalamus membranaceus', 'https://api.aiplants.cn/resource/1/%E9%BB%84%E7%AB%B9/1546546.jpg#https://api.aiplants.cn/resource/1/%E9%BB%84%E7%AB%B9/865696.jpg#https://api.aiplants.cn/resource/1/%E9%BB%84%E7%AB%B9/892840.jpg#https://api.aiplants.cn/resource/1/%E9%BB%84%E7%AB%B9/892858.jpg', '禾本科', '牡竹属', '云南省南方比较习见的野生竹种，可用作造纸原料，建筑用材。', '', '竿可用作造纸原料，建筑用材，扁担和劈细做筷子。笋漂洗加工可制作笋丝。\n', '产云南东南部至西南部，四川可能已栽培。', '生于海拔1000米以下的低山与河谷地区。', '竿高8-15米（原记载为20-23米），直径7-10厘米，梢端略弯曲，基部第一至第三节环列气根；节间长34-42厘米，幼时被白粉；竿环平；箨环强隆起；节内长8毫米；主枝3枝，其余枝条较细，在竿上部者可下垂。箨鞘早落性，厚纸质至革质，通常较其所在的节间为长（变型花竿黄竹和流苏黄竹的可较短于节间），背面有白粉及易落的黑褐色小刺毛；箨耳长5毫米，宽1毫米其上生有长为0.5-1厘米的繸毛数条；箨舌高0.8-1厘米，腹面多毛茸，边缘具粗锯齿；箨片外翻，窄长形，长30-40厘米，宽约2.5厘米，占鞘口之1/2-1/3两面均被棕色小硬毛，尤以腹面的基部处为多。末级小枝具3-6叶；叶鞘具纵肋；叶耳镰形，其上具紫色缝毛数条；叶舌不明显，高仅1毫米，腹面具毛，先端波状浅裂；叶片披针形，质薄，长12.5-25厘米，宽1.2-2厘米，基部宽楔形至圆形，先端渐窄，最后扭转并具粗糙的长尖头，两面均具柔毛，叶缘具向下的小锯齿而粗糙，次脉4-7对，再次脉7-9条。花枝有大型呈圆锥花序状的分枝，节间长2.5-5厘米，无毛，或上部常具白粉，节上密集丛生多枚假小穗，形成球形的簇团，其直径为2.5-5厘米（原记载则是1.7-2.5厘米）；小穗微扁，近于无毛而有光泽，长1-1.3厘米，宽2.5-3毫米，初为黄绿色，干燥后为枯草色，质地较软，含成熟小花2-5朵；颖2片，卵形，先端钝圆或尖锐；外稃与颖相类似而较大，长8-9毫米，宽5-8毫米，质地薄；近于膜质，无毛，惟常在边缘生纤毛，先端具1短芒刺状小尖头，其长约1毫米；内稃亦为膜质，长7-8毫米，宽1.4毫米，下部小花者背部具2脊，脊上生纤毛，脊间3脉，先端钝或为凹头，最上部的小花内稃圆卷而无2脊，或亦具2脊，则脊上无毛；雄蕊成熟后伸出花外，花丝细而长；花药黄色至紫色，长4毫米，先端具短的小尖头；子房卵形，较细长，上部生毛而下部无毛；花柱长5-6毫米，全体被微毛，柱头1，紫色，羽毛状。果实广卵形，基部圆，长5-7.5毫米，一侧具沟槽或微扁，先端具长喙，胚明显。\n\n', '', 1527331073, 1527340671, 0);
INSERT INTO `plant` VALUES (35, '肾耳唐竹', '', 'Sinobambusa nephroaurita', 'https://api.aiplants.cn/resource/1/%E8%82%BE%E8%80%B3%E5%94%90%E7%AB%B9/871992.jpg#https://api.aiplants.cn/resource/1/%E8%82%BE%E8%80%B3%E5%94%90%E7%AB%B9/872065.jpg#https://api.aiplants.cn/resource/1/%E8%82%BE%E8%80%B3%E5%94%90%E7%AB%B9/872239.jpg#https://api.aiplants.cn/resource/1/%E8%82%BE%E8%80%B3%E5%94%90%E7%AB%B9/894807.jpg', '禾本科', '唐竹属', '箨鞘革质早落性，近长圆形，背面起初为绿色或黄褐色，箨舌拱形，全缘。可做观赏植物，也可用材。', '', '观赏；材用。', '产四川、广东和广西。', '生于山坡、林下或山谷中。常成片生长。海拔40—1500米。\n', '竿高6-8米，径粗2-3厘米；节间长30-45厘米，起初略被白粉和白色粗毛，具隆起之纵肋，中空，竿髓稍呈屑状，分枝以下的节间圆柱状，分枝以上之节间则下半部扁平；箨环明显，初被棕色刚毛，后呈木栓质隆起而无毛；竿环隆起，节下方具白粉环。箨鞘革质早落性，近长圆形，背面起初为绿色或黄褐色，疏生紫褐色刺毛，具纵肋，边缘具黄褐色纤毛或至无毛，基部密生深褐色向下刚毛；箨舌高2-3毫米，拱形，全缘，先端具细纤毛或近无毛；箨耳发达肾形，最大者长可达15毫米，宽9毫米，一般的长为7-8毫米，宽4-5毫米；表面粗糙，繸毛呈放射状，其长为10-15毫米；箨片通常外翻或开展，竿先端者狭披针形直立，竿中部的箨片为三角形至披针形；两面均被白柔毛，边缘生有锯齿，先端渐尖至急尖，基部略向内收窄。竿每节分3-5枝，分枝节间扁平。末级小枝具4-6叶；叶鞘长5厘米，具隆起之纵肋，表面无毛，边缘生纤毛；叶耳微弱或不发达，繸毛直立，白色，长达1厘米；叶舌甚短，先端截平或略呈拱形；叶片宽披针形至狭披针形，质薄，长11-18厘米，宽11-16毫米，两面无毛，边缘生小锯齿，基部渐尖或钝圆，先端渐尖或急尖，次脉4-6对，两面均可见小横脉，叶柄长2-4毫米，无毛。花枝未见。', '笋期4-5月。', 1527331087, 1527331087, 0);
INSERT INTO `plant` VALUES (36, '生石花', '', 'Lithops pseudotruncatella subsp. archerae', 'https://api.aiplants.cn/resource/1/%E7%94%9F%E7%9F%B3%E8%8A%B1/16_585x436.jpg#https://api.aiplants.cn/resource/1/%E7%94%9F%E7%9F%B3%E8%8A%B1/229_640x503.jpeg#https://api.aiplants.cn/resource/1/%E7%94%9F%E7%9F%B3%E8%8A%B1/2_500x386.jpg#https://api.aiplants.cn/resource/1/%E7%94%9F%E7%9F%B3%E8%8A%B1/5_568x443.jpg', '番杏科', '生石花属', '生石花形态奇特，花色艳丽，使得许多爱好者专门收集栽培，具有很高的观赏价值。', '', '生石花形态别致，株型小巧，高度肉质，叶形、叶色及花色都富于变化，而栽培又有一定难度。显得既可爱又神秘，也以此成为多肉植物爱好者的收集热点。又因其形如彩石，色彩丰富，享有“有生命的石头”的美称。生石花品种繁多，精致自然，是市场上较受欢迎的小型多肉植物之一。', '多地有栽培。', '喜温暖干燥和阳光充足环境，怕低温，忌强光暴晒，该属植物生长适温为10-30℃，冬季温度不低于12℃，可短暂耐受4-5℃的低温。宜生长在疏松透气的中性沙壤土。', '生石花为多年生肉质草本。植株具肥大肉质根，但在自然状态下主根很少有侧根，仅在逐渐变细的主根末端有少数须根连有毛细根，当主根折断后会萌发侧根。根状茎极短，植株卵圆形，高2-3.5厘米。 地上部分是两片对生联结的肉质叶，呈倒圆锥体，叶色白、浅灰、棕、蓝灰、灰绿、红、紫红等。叶楔形、半圆形、椭圆形或肾形，对称或不对称。单株叶长18-50毫米，宽13-38毫米，群生植株2-50头。 植株顶部具有深色枝状凹陷花纹，称作“视窗”；该属植物叶肉透明，叶片底部富含叶绿体，阳光从视窗进入植株内部并进行光合作用。植株顶部具一裂缝，深约1-4毫米，裂缝中开花，花单生，雏菊状，花茎2-3厘米，花白或黄色，花期盛夏至中秋。蒴果5-9室，种子黄褐色、淡黄褐色或褐色，有斑点。', '', 1527331094, 1527331094, 0);
INSERT INTO `plant` VALUES (37, '玉露', '', 'Haworthia cooperi var. pilifera', 'https://api.aiplants.cn/resource/1/%E7%8E%89%E9%9C%B2/151_731x621.jpg#https://api.aiplants.cn/resource/1/%E7%8E%89%E9%9C%B2/153_4032x2982.jpg#https://api.aiplants.cn/resource/1/%E7%8E%89%E9%9C%B2/172_677x483.jpg#https://api.aiplants.cn/resource/1/%E7%8E%89%E9%9C%B2/173_650x420.jpg', '阿福花科', '十二卷属', '玉露植株玲珑小巧，叶色晶莹剔透，富于变化，是近年来在国内人气较旺的小型多肉植物之一。其变种、形态繁多，也衍生出了许多经典的园艺品种。', '', '', '原产南非，现世界多地有栽培。', '生长期应给予充足的光照，玉露喜欢有一定空气湿度的环境，空气干燥时可经常向植株及周围环境喷水，以增加空气湿度，在生长季节还可用剪去上半部的透明无色饮料瓶将植株罩起来养护，使其在空气湿润的小环境中生长，这样可使叶片饱满，“窗”的透明度也更高。', '多年生肉质草本植物，植株初为单生，以后逐渐呈群生状。肉质叶呈紧凑的莲座状排列，叶片肥厚饱满，翠绿色，上半段呈透明或半透明状，称为“窗”，有深色的线状脉纹，在阳光较为充足的条件下，其脉纹为褐色，叶顶端有细小的“须”。 花葶腋生，总状花序，小花白色。', '', 1527331097, 1527331097, 0);
INSERT INTO `plant` VALUES (38, '膜果麻黄', '', 'Ephedra przewalskii', 'https://api.aiplants.cn/resource/1/%E8%86%9C%E6%9E%9C%E9%BA%BB%E9%BB%84/2715983.jpg#https://api.aiplants.cn/resource/1/%E8%86%9C%E6%9E%9C%E9%BA%BB%E9%BB%84/2741229.jpg#https://api.aiplants.cn/resource/1/%E8%86%9C%E6%9E%9C%E9%BA%BB%E9%BB%84/2741232.jpg#https://api.aiplants.cn/resource/1/%E8%86%9C%E6%9E%9C%E9%BA%BB%E9%BB%84/2741241.jpg', '麻黄科', '麻黄属', '', '', '有固沙作用，茎枝可作燃料。', '产于内蒙古、宁夏、甘肃北部、青海北部、新疆天山南北麓，', '常生于干燥沙漠地区及干旱山麓，多砂石的盐碱土上也能生长，在水分稍充足的地区常组成大面积的群落，或与梭梭、怪柳、沙拐枣等旱生植物混生。', '灌木，高50-240厘米；木质茎明显，为植株高度的1/2或更高，基部径约1厘米或更粗，茎皮灰黄色或灰白色，细纤维状，纵裂成窄椭圆形网眼；茎的上部具多数绿色分枝，老枝黄绿色，纵槽纹不甚明显，小枝绿色，2-3枝生于节上，分枝基部再生小枝，形成假轮生状，每节常有假轮生小枝9-20或更多，小枝节间粗长，长2.5-5厘米，径2-3毫米。叶通常3裂并有少数2裂混生，卞部1/2-2/3合生，裂片三角形、或长三角形，先端急尖或具渐尖的尖头。球花通常无梗，常多数密集成团状的复穗花序，对生或轮生于节上；雄球花淡褐色或褐黄色，近圆球形；，径2-3毫米，苞片3-4轮， 每轮3片，稀2片对生，膜质，黄色或淡黄绿色，中央有绿色草质肋，三角状宽卵形或宽倒卵形，仅基部合生，假花被宽扁而拱凸似蚌壳状，雄蕊7-8，花丝大部合生，先端分离，花药有短梗；雌球花淡绿褐色或淡红褐色，近圆球形，径3-4毫米，苞片4-5轮，每轮3片，稀2片对生，干燥膜质，仅中央有较厚的绿色部分，扁圆形或三角状扁卵形，几全部离生，基部窄缩成短柄状或具明显的爪，最上一轮或一对苞片各生一雌花，胚珠窄卵圆形，顶端1/4处常窄缩成颈状，珠被管长1.5-2毫米，伸于苞片之外，直立、弯曲或卷曲，裂口约占全长的1/2. 雌球花成熟时苞片增大成千燥半透明的薄膜状，淡棕色；种子通常3粒，稀2粒，包于干燥膜质苞片内，暗褐红色，长卵圆形，长约4毫米，径2-2.5毫米，顶端细窄成尖突状，表面常有细密纵皱纹。', '', 1527331098, 1527331098, 0);
INSERT INTO `plant` VALUES (39, '云杉', '茂县云杉、茂县杉、异鳞云杉', 'Picea asperata', 'https://api.aiplants.cn/resource/1/%E4%BA%91%E6%9D%89/1235977.jpg#https://api.aiplants.cn/resource/1/%E4%BA%91%E6%9D%89/1271700.jpg#https://api.aiplants.cn/resource/1/%E4%BA%91%E6%9D%89/1387819.jpg#https://api.aiplants.cn/resource/1/%E4%BA%91%E6%9D%89/53025.jpg', '松科', '云杉属', '为乔木。为中国特有树种。树皮淡灰褐色或淡褐灰色，裂成不规则鳞片或稍厚的块片脱落。包头市将云杉定为包头市市树。', '幽栖尘想绝，岩阁倚杉松。\n吟思禅中尽，霜髭病后浓。\n溪闲澄夜月，山静答秋钟。\n寂寞怀高趣，残阳独倚筇。\n--《寄石城行光长老》朝代：宋 作者：释智圆', '树干高大通直，节少，材质略轻柔，纹理直、均匀，结构细致，易加工，具有良好的共鸣性能。可供建筑、飞机、乐器（钢琴、提琴）、舟车、家具、器具、箱盒、刨制胶合板与薄木以及木纤维工业原料等用材。', '产于陕西西南部、甘肃东部、四川。', '耐阴、耐寒、喜欢凉爽湿润的气候和肥沃深厚、排水良好的微酸性沙质土壤。', '乔木，高达45米，胸径达1米；树皮淡灰褐色或淡褐灰色，裂成不规则鳞片或稍厚的块片脱落；小枝有疏生或密生的短柔毛，或无毛，一年生时淡褐黄色、褐黄色、淡黄褐色或淡红褐色，叶枕有白粉，或白粉不明显，二、三年生时灰褐色，褐色或淡褐灰色；冬芽圆锥形，有树脂，基部膨大，上部芽鳞的先端微反曲或不反曲，小枝基部宿存芽鳞的先端多少向外反卷。主枝之叶辐射伸展，侧枝上面之叶向上伸展，下面及两侧之叶向上方弯伸，四棱状条形，长1-2厘米，宽1-1.5毫米，微弯曲，先端微尖或急尖，横切面四棱形，四面有气孔线，上面每边4-8条，下面每边4-6条。球果圆柱状矩圆形或圆柱形，上端渐窄，成熟前绿色，熟时淡褐色或栗褐色，长5-16厘米，径2.5-3.5厘米；中部种鳞倒卵形，长约2厘米，宽约1.5厘米，上部圆或截圆形则排列紧密，或上部钝三角形则排列较松，先端全缘，或球果基部或中下部种鳞的先端两裂或微凹；苞鳞三角状匙形，长约5毫米；种子倒卵圆形，长约4毫米，连翅长约1.5厘米，种翅淡褐色，倒卵状矩圆形；子叶6-7枚，条状锥形，长1.4-2厘米，初生叶四棱状条形，长0.5-1.2厘米，先端尖，四面有气孔线，全缘或隆起的中脉上部有齿毛。', '花期4-5月，球果9-10月成熟。', 1527340232, 1527340434, 0);
INSERT INTO `plant` VALUES (40, '垂柳', '水柳、垂丝柳、清明柳', 'Salix babylonica', 'https://api.aiplants.cn/resource/1/%E5%9E%82%E6%9F%B3/1099598.jpg#https://api.aiplants.cn/resource/1/%E5%9E%82%E6%9F%B3/1099601.jpg#https://api.aiplants.cn/resource/1/%E5%9E%82%E6%9F%B3/128835.jpg#https://api.aiplants.cn/resource/1/%E5%9E%82%E6%9F%B3/2700650.jpg', '杨柳科', '柳属', '落叶乔木，高可达18米，胸径1米，树冠倒广卵形，小枝细长下垂；披针形或线状披针形叶片，有锯齿；早春先叶开花，雌雄异株，葇荑花序直立；蒴果，种子小，有白色丝状长毛。', '梨花风起正清明，游子寻春半出城。\n日暮笙歌收拾去，万株杨柳属流莺。”\n--《苏堤清明即事》朝代：宋 作者：吴惟信\n', '园林用途\n枝条细长，生长迅速，宜配植在水边，如桥头、池畔、河流，湖泊等水系沿岸处。与桃花间植可形成桃红柳绿之景，是江南园林春景的特色配植方式之一。也可作庭荫树、行道树、公路树。亦适用于工厂绿化，还是固堤护岸的重要树种。\n经济价值\n木材可供制家具；枝条可编筐；树皮含鞣质，可提制栲胶。叶可作羊饲料。\n', '产长江流域与黄河流域，其他各地均栽培，在亚洲、欧洲、美洲各国均有引种。', '控制新育苗，移栽、定植过密苗木。垂柳枝叶稀疏、根系较深。可移植到路、渠两侧培养，对大田农作物影响较轻。培养胸径4厘米～6厘米的苗木。定植密度为每亩500株，重点培养干形。主枝选择三四个方向合适、相距40～50厘米、相互错落分布的健壮枝短截，短截枝不宜超过主干的1/3。垂柳易发生蚜虫、柳毒蛾、天牛等，要注意及时防治。', '大落叶乔木，分布广泛，生命力强。是常见的树种之一，垂柳也是园林绿化中常用的行道树，观赏价值较高，成本低廉，深受各地绿化喜爱。主要分布浙江，湖南，江苏，安徽等地。小枝细长下垂，淡黄褐色。叶互生，披针形或条状披针形，长8～16cm，先端渐长尖，基部楔形，无毛或幼叶微有毛，具细锯齿，托叶披针形。雄蕊2，花丝分离，花药黄色，腺体2。雌花子房无柄，腺体1。', '花期3～4月；果熟期4～6月。', 1527340484, 1527342900, 0);
INSERT INTO `plant` VALUES (41, '花粉麻竹', '', 'Dendrocalamus pulverulentus var. amoenus', 'https://api.aiplants.cn/resource/1/%E8%8A%B1%E7%B2%89%E9%BA%BB%E7%AB%B9/1061648.jpg#https://api.aiplants.cn/resource/1/%E8%8A%B1%E7%B2%89%E9%BA%BB%E7%AB%B9/2549386.jpg#https://api.aiplants.cn/resource/1/%E8%8A%B1%E7%B2%89%E9%BA%BB%E7%AB%B9/864780.jpg#https://api.aiplants.cn/resource/1/%E8%8A%B1%E7%B2%89%E9%BA%BB%E7%AB%B9/892497.jpg', '禾本科', '牡竹属', '乔木状竹类（个别种可为半攀援性）,属于地下茎合轴型。', '', '', '分布于福建南部、台湾、广东、香港、广西、海南、四川、贵州、云南和西藏南部等省区，其中尤以云南的种类最多。', '', '乔木状竹类（个别种可为半攀援性）。地下茎合轴型。竿单丛生长，直立而略向外方倾斜，其梢端则常可下垂；节间圆筒形，竿壁厚（碟环慈组Sect. Patellares的种类例外），甚至近于实心；竿每节具多枝，主枝发达或否，决不具短缩的枝刺。箨鞘脱性（或个别种在竿基部数节的能留存较久），多为革质；箨耳常不明显或不存在；箨舌较明显；箨片常外翻，少有直立的。末级小枝具多叶；叶鞘幼时被微毛，以后无毛；叶耳常不明显而叶舌发达；叶片大小在同一种或同一植株上亦可变异较大，通常为大型而甚宽，基部楔形或宽楔形，小横脉明显或否，或以透明微点代替之；叶柄短。花枝无叶或有时具叶，常可多分枝而呈大形圆锥状；假小穗以数枚乃至多数枚丛生于花枝及其分枝之各节，多枚者有时密集成头状或球形；苞片1-4，最上方1片腋内常无芽；小穗卵形至钻形，含1朵（我国不产）乃至多朵小花，顶生小花常不孕或退化；小穗轴极短，（成熟时有些种类的可稍延伸而使小花彼此疏离），无关节，故不在诸小花间逐节折断而仅脱节于颖下；颖通常1-3片，卵圆形，具多脉，先端急尖或具短的小尖头；外稃似颖，但较大，向小穗顶端逐次渐变窄长，有时先端具芒刺状小尖头；内稃在下方小花中具2脊，而最上1朵两性小花（或仅具一朵小花者）无2脊，甚至背部圆卷，或梢可具脊，则其脊上无毛，此外也有的种类之内稃均具2脊；鳞被常缺，或有时具1-3片退化鳞被；雄蕊11，花丝分离或其基部有时能作各式的靠接，但仍易分离，花药的药室之上下两端稍有分离或药隔向上伸出为微小尖头；子房球形或卵形，体被柔毛，子房柄短或无；花柱与柱头单一（个别种柱头可为2或3枚），后者常弯曲，具羽毛状毛茸。果小形，亦可呈坚果状而较大，卵形或长椭圆形，果皮薄而为囊果状（麻竹亚属）乃至厚坚而为坚果状（牡竹亚属）。笋期多在夏季。', '', 1527340673, 1527340673, 0);
INSERT INTO `plant` VALUES (42, '毛脚龙竹', '', 'Dendrocalamus barbatus var. internodiiradicatus', 'https://api.aiplants.cn/resource/1/%E6%AF%9B%E8%84%9A%E9%BE%99%E7%AB%B9/1189266.jpg#https://api.aiplants.cn/resource/1/%E6%AF%9B%E8%84%9A%E9%BE%99%E7%AB%B9/256104.jpg#https://api.aiplants.cn/resource/1/%E6%AF%9B%E8%84%9A%E9%BE%99%E7%AB%B9/894198.jpg#https://api.aiplants.cn/resource/1/%E6%AF%9B%E8%84%9A%E9%BE%99%E7%AB%B9/894206.jpg', '禾本科', '牡竹属', '竿高15-18米，直径10-15厘米，竿基部数节间的外面均密生气根，有如网状包裹着节间，箨耳和箨片上表面之基部几无繸毛生长，叶片常较大，次脉7-9对，小穗含2或3朵小花。', '', '', '云南西双版纳。', '', '竿高15-18米，直径10-15厘米，梢端弯或微下垂，基部数节间的外面均密生气根，有如网状包裹着节间；节间的下部深绿色，长26-32厘米，一侧扁平部分无毛，上部具少量白粉，竿壁厚2-2.5厘米；竿环不隆起，节内长1.5厘米，在其间和箨环下方均具一圈白色绒毛环。枝下高0.5-1米。竿每节分多枝，主枝3条，其中1条明显较粗壮，或有时主枝不发达而有一大型潜伏芽，侧枝纤细，下垂。箨鞘早落性，革质，初期为黄棕色，背部被白粉和生成棕色小刺毛；箨耳与箨片基部之外延部分相连，波状，长5-15毫米，宽2-3毫米，其上几无繸毛生长；箨舌高5-8毫米，先端具不整齐的齿裂；箨片外翻，腹面的基部也密被猪鬃状硬直毛，其余部位被小刺毛。末级小枝具8-15叶；叶鞘被微毛；叶耳微小，易落，其上具繸毛数条；叶舌高1毫米；叶片常较大，长10-15厘米，宽1-2厘米，次脉7-9对。花枝无叶，节间长1.5-1.8厘米，一侧扁平或具沟槽，其间密被黄棕色绒毛，每节着生10-25枚假小穗，其簇团的直径为1-2.2厘米；小穗倒卵形，长6-8.5毫米，宽2-4毫米，黄绿色，近于无毛，含2或3朵小花；颖2或3片；外稃长6-7毫米，宽4-5毫米，先端具长为0.8-1毫米的芒刺状小尖头；内稃长5-6毫米，两脊间宽1毫米，具3脉；花丝长6毫米，花药黄色或干后带紫色，长亦为6毫米，先端具尖头；雌蕊全长6-7.5毫米，子房上部连同花柱及柱头均被毛茸。果实未见。', '花期6-10月。', 1527340675, 1527340675, 0);
INSERT INTO `plant` VALUES (43, '斑苦竹', '光竹、广西苦竹', 'Pleioblastus maculatus', 'https://api.aiplants.cn/resource/1/%E6%96%91%E8%8B%A6%E7%AB%B9/1986528.jpg#https://api.aiplants.cn/resource/1/%E6%96%91%E8%8B%A6%E7%AB%B9/1986533.jpg#https://api.aiplants.cn/resource/1/%E6%96%91%E8%8B%A6%E7%AB%B9/593780.jpg#https://api.aiplants.cn/resource/1/%E6%96%91%E8%8B%A6%E7%AB%B9/859022.jpg', '禾本科', '苦竹属', '地下茎复轴型。竿直立，高3-8米，粗1.5-4厘米。主要分布于长江流域各省 的亚热带常绿阔叶林地区, 形成乔木层单优势种的群落. 斑苦竹的枝叶 是大熊猫(动物园) 的主要食物之一,在生产实践上也具有重要的经济价值. 植物种群的数量特征是植物种群的基本特征之一。', '', '笋味苦，宜处理后方可食用。篾性一般，可破篾；可作篱笆和供农作物搭棚架。', '江苏、江西、福建、广东、广西、四川、贵州、云南等省区。', '生于密丛林中或偏阴的山坡。', '地下茎复轴型。竿直立，高3-8米，粗1.5-4厘米，幼竿绿色，厚被脱落性白粉，箨环密具一圈棕色毛，节下方具直立近于向下的白色短纤毛，其余部分则光滑无毛，老竿黄绿色，被少量灰黑色粉垢；节间圆筒形，在分枝一侧的基部微凹；箨环与竿环均突出，近无毛；箨环残留有箨鞘基部的木栓质残留物；竿每节具3-5枝，枝与主竿成400-500的夹角。箨鞘棕红略带紫绿色，迟落，长为节间的3/4近革质，背部以有丰富油脂而具光泽，常具或密或稀的棕色小斑点，尤以箨鞘上部（或下部）为较密集，除箨鞘基部密具棕色倒向刺毛外，余处无毛，边缘全缘，无纤毛；箨耳无或呈点状、卵圆状、棕色，边缘有几条短而通直或曲折且易落的繸毛；箨舌深棕红色，低矮截形或微凹或凸出，顶端全缘，无纤毛。箨片绿带紫色，线状披针形，呈狭条状，外翻而下垂，基部略向内收窄，近基部为棕红色，被微毛，略粗糙，先端渐尖，两边缘具极稀疏的细齿，几全缘。末级小枝具3-5叶；叶鞘绿色，向边缘具极稀少的柔毛，一边具短细纤毛，另一边膜质无毛；无叶耳和繸毛；叶舌高1-2毫米，背面具粗毛，顶端截形，边缘具短纤毛；叶柄长约4毫米；叶片披针形，长8.8-18.5厘米，宽13.4-29毫米，先端长渐尖，基部宽楔形，上表面无毛，下表面近基部和主脉则具易脱落的短毛，次脉5-7对，叶缘两侧具细锯齿。圆锥花序常侧生于花枝各节；小穗具8-15朵小花；颖2，纸质，第一颖长约10毫米，具11-13脉，先端稍钝，第二颖长约9.5毫米，具11脉，先端具小尖头；外稃厚纸质，背部光亮无毛，但被少量白粉，长约8毫米，宽约7.5毫米，具7-10脉；内稃长约8毫米，成熟后可更长，2脊上具纤毛，脊间具4-6脉，背部有细毛，脊外至边缘各具2脉；鳞被3，大小近等，中间一片披针形，具10脉，侧面的2片卵状椭圆形，各具11-12脉，先端均具长纤毛；子房瓶状，长约8毫米，花柱长约1.5毫米；柱头3，羽毛状。果实椭圆形。', '笋期5月上旬至6月初。', 1527341543, 1527343001, 0);
INSERT INTO `plant` VALUES (44, '龟背竹', '蓬莱蕉、龟背蕉、龟背、电线草', 'Monstera deliciosa', 'https://api.aiplants.cn/resource/1/%E9%BE%9F%E8%83%8C%E7%AB%B9/1208999.jpg#https://api.aiplants.cn/resource/1/%E9%BE%9F%E8%83%8C%E7%AB%B9/1222316.jpg#https://api.aiplants.cn/resource/1/%E9%BE%9F%E8%83%8C%E7%AB%B9/1394222.jpg#https://api.aiplants.cn/resource/1/%E9%BE%9F%E8%83%8C%E7%AB%B9/1731177.jpg', '天南星科', '龟背竹属', '天南星科(Araceae)龟背竹属(Monstera)的一种多年生木质藤本攀缘性常绿灌木，别名蓬莱蕉、龟背蕉、电线兰，生于林中，攀缘树上。 龟背竹汁液有毒，对皮肤有刺激和腐蚀作用。食用未成熟的果实会引起口舌灼伤。', '', '龟背竹在欧美、日本常用于盆栽观赏。南美国家巴西、阿根廷和美洲中部的墨西哥除盆栽以外，常种在廊架或建筑物旁，是极好的垂直绿化材料。\n龟背竹有晚间吸收二氧化碳的功效，对改善室内空气质量有很大帮助。具有优先吸附甲醛、苯、TVOC等有害气体的特点，是一种理想的室内植物。\n果实味美可食，但常具麻味。果实成熟后可用来做菜食，有甜味，香味像凤梨或香蕉。但要注意果实未成熟不能吃，因为有较强的刺激性。在原产地居民称这种果实为“神仙赐给的美果”。', '原产墨西哥，各热带地区多引种栽培供观赏。福建、广东、云南栽培于露地，北京、湖北等地多栽于温室。', '喜温暖潮湿环境，切忌强光暴晒和干燥，耐阴，易生长于肥沃疏松、吸水量大、保水性好的微酸性壤土，以腐叶土或泥炭土最好。光照：夏季需避开强烈的光照，但冬季应光照充足。 温度：最适宜生长的温度为15～20℃，气温超过30℃或低于5℃则生长停滞。', '攀援灌木。茎绿色，粗壮，有苍白色的半月形叶迹，周延为环状，余光滑，长3-6米，粗6厘米，节间长6-7厘米，具气生根。叶柄绿色，长常达1米，腹面扁平，宽4-5厘米，背面钝圆，粗糙，边缘锐尖，基部甚宽，对折抱茎，排列为覆瓦状，形如鸢尾，两侧叶鞘宽，向上渐狭，脱落后叶柄边缘成绉波状；叶片大，轮廓心状卵形，宽40-60厘米，厚革质，表面发亮，淡绿色，背面绿白色，边缘羽状分裂，侧脉间有1-2个较大的空洞，靠近中肋者多为横圆形，宽1.5-4厘米，向外的为横椭圆形，宽5-6厘米；中肋及侧脉表面绿色，背面绿白色，两面均隆起；I次侧脉8-10对，基部的相互靠近，向上渐远离，II、III、IV级叶脉网状，不明显。花序柄长15-30厘米，粗1-3厘米，绿色，粗糙。佛焰苞厚革质，宽卵形，舟状，近直立，先端具喙，长20-25厘米，人为展平宽15-17.5厘米，苍白带黄色。肉穗花序近圆柱形，长17.5-20厘米，粗4-5厘米，淡黄色。雄蕊花丝线形，花粉黄白色。雌蕊陀螺状，长7-8毫米，柱头小，线形，纵向，黄色，稍凸起。浆果淡黄色，柱头周围有青紫色斑点，长1厘米，粗7.5毫米。', '花期8-9月，果于异年花期之后成熟。', 1527343065, 1527343065, 0);
INSERT INTO `plant` VALUES (45, '青丝黄竹', '', 'Bambusa eutuldoides var. viridivittata', 'https://api.aiplants.cn/resource/1/%E9%9D%92%E4%B8%9D%E9%BB%84%E7%AB%B9/1689163.jpg#https://api.aiplants.cn/resource/1/%E9%9D%92%E4%B8%9D%E9%BB%84%E7%AB%B9/871543.jpg#https://api.aiplants.cn/resource/1/%E9%9D%92%E4%B8%9D%E9%BB%84%E7%AB%B9/871549.jpg#https://api.aiplants.cn/resource/1/%E9%9D%92%E4%B8%9D%E9%BB%84%E7%AB%B9/871558.jpg', '禾本科', '簕竹属', '多栽培于村落附近及溪河两岸，是竹亚科孝顺竹亚属植物大眼竹的一个变种。', '', '竹竿色彩夺目，非常美观，堪称园艺佳品，宜种植庭园中以供观赏。', '产广东、广西，香港有栽培。', '', '竿高6-12米，直径4-6厘米，尾梢略弯，下部挺直；节间长30-40厘米，幼时薄被白蜡粉或近于无粉，无毛或有时仅于上半部疏生脱落性小刺毛；竿壁厚5毫米，节处稍有隆起，竿基部数节于箨环之上下方各环生一圈灰白色绢毛；分枝常自竿基部第二或第三节开始，以数枝乃至多枝簇生，其中3枝较为粗长。箨鞘早落，革质，背面通常无毛，或有时被极稀疏的脱落性贴生小刺毛，干时纵肋稍有隆起，近外侧边缘一边有时具数条纵向黄白色细条纹，先端向外侧一边长下斜，呈极不对称的拱形；箨耳极不相等，形状各异，质极脆，略皱，边缘被波曲状细刚毛，大耳极下延，其下延程度可达箨鞘全长的2/5-1/2，倒披针形至狭长圆形，长5-6.5厘米，宽约1.5厘米，小耳近圆形或长圆形，直径约1厘米，或有时完全与箨片基部相连，故难以区分；箨舌高3-5毫米，边缘呈不规则齿裂或条裂，被短流苏状毛；箨片直立，易脱落，呈不对称的三角形至狭三角形，背面疏生脱落性小刺毛，腹面近基部脉间被棕色小刺毛而上部粗糙，先端渐尖具锐利硬尖头，基部略微收窄后即向两侧外延与箨耳相连，此相连部分约为1厘米，箨片基部宽度约为箨鞘先端宽的3/5。叶鞘无毛，背部具脊，纵肋隆起；叶耳有时不存在，存在时则呈卵形或狭倒卵形乃至倒卵状长圆形，边缘具数条直或曲的细长繸毛；叶舌高约0.5毫米，截形，边缘具微齿；叶片披针形至宽披针形，一般长12-25厘米，宽14-25毫米，上表面无毛，下表面密生短柔毛，先端骤渐尖具粗糙钻状尖头，基部近圆形或楔形。假小穗以数至多枚簇生花枝各节，线形，长2.5-5.5厘米；小穗含小花5-6朵，基部承托以数片具芽苞片；小穗轴节间形扁，长3-4毫米，顶端膨大且在其边缘被短纤毛；颖仅1片，长圆形，长9-10毫米，背面无毛，但有许多极小的紫色斑点，具11脉，先端急尖具短尖头；外稃与颖相似，但长12-13毫米，具13-15脉；内箨披针形，长约11毫米，具2脊，脊近顶端被短纤毛，脊间4脉，脊外每边各具2脉；鳞被3，不相等，边缘被长纤毛，前方2片较窄，长约2毫米，后方1片较大，宽倒卵形或近圆形，长约2毫米；花药长5毫米，顶端又开；子房近球状，直径约1毫米，顶端被短硬毛，花柱极短，被短硬毛，柱头3，羽毛状。颖果幼时近倒卵状，长约5毫米，顶部被短硬毛，并留 有柱头残余。', '', 1527343408, 1527343408, 0);
INSERT INTO `plant` VALUES (46, '三球悬铃木', '祛汗树、法国梧桐、悬铃木', 'Platanus orientalis', 'https://api.aiplants.cn/resource/1/%E4%B8%89%E7%90%83%E6%82%AC%E9%93%83%E6%9C%A8/1236025.jpg#https://api.aiplants.cn/resource/1/%E4%B8%89%E7%90%83%E6%82%AC%E9%93%83%E6%9C%A8/1440022.jpg#https://api.aiplants.cn/resource/1/%E4%B8%89%E7%90%83%E6%82%AC%E9%93%83%E6%9C%A8/1487996.jpg#https://api.aiplants.cn/resource/1/%E4%B8%89%E7%90%83%E6%82%AC%E9%93%83%E6%9C%A8/1783030.jpg', '悬铃木科', '悬铃木属', '落叶大乔木，又叫裂叶悬铃木、鸠摩罗什树。', '', '世界著名的优良庭荫树和行道树，有“行道树之王”之称。', '', '喜光，喜湿润温暖气候，较耐寒。对土壤要求不严，但适生于微酸性或中性、排水良好的土壤，微碱性土壤虽能生长，但易发生黄化。根系分布较浅，台风时易受害而倒斜。', '落叶大乔木，高达30米，树皮薄片状脱落；嫩枝被黄褐色绒毛，老枝秃净，干后红褐色，有细小皮孔。叶大，轮廓阔卵形，宽9-18厘米，长8-16厘米，基部浅三角状心形，或近于平截，上部掌状5-7裂，稀为3裂，中央裂片深裂过半，长7-9厘米，宽4-6厘米，两侧裂片稍短，边缘有少数裂片状粗齿，上下两面初时被灰黄色毛被，以后脱落，仅在背脉上有毛，掌状脉5条或3条，从基部发出；叶柄长3-8厘米，圆柱形，被绒毛，基部膨大；托叶小，短于1厘米，基部鞘状。花4数；雄性球状花序无柄，基部有长绒毛，萼片短小，雄蕊远比花瓣为长，花丝极短，花药伸长，顶端盾片稍扩大；雌性球状花序常有柄，萼片被毛，花瓣倒披针形，心皮4个，花柱伸长，先端卷曲。果枝长10-15厘米，有圆球形头状果序3-5个，稀为2个；头状果序直径2-2.5厘米，宿存花柱突出呈刺状，长3-4毫米，小坚果之间有黄色绒毛，突出头状果序外。', '', 1527347129, 1527347129, 0);
INSERT INTO `plant` VALUES (47, '苦糖果', '', 'Lonicera fragrantissima var. lancifolia', 'https://api.aiplants.cn/resource/1/%E8%8B%A6%E7%B3%96%E6%9E%9C/1971318.jpg#https://api.aiplants.cn/resource/1/%E8%8B%A6%E7%B3%96%E6%9E%9C/256718.jpg#https://api.aiplants.cn/resource/1/%E8%8B%A6%E7%B3%96%E6%9E%9C/2700106.jpg#https://api.aiplants.cn/resource/1/%E8%8B%A6%E7%B3%96%E6%9E%9C/2753743.jpg', '忍冬科', '忍冬属', '落叶灌木，观赏性植物。', '', '可用于祛风除湿，清热止痛。主治风湿关节痛；外用治疔疮。', '产河北南部（内丘）、河南西南部（西峡）、湖北西部（兴山）、安徽南部、浙江东部（天台山）及江西北部（鞋山）。', '生于向阳山坡林中、灌丛中或溪涧旁。', '半常绿或有时落叶灌木，高达2米；幼枝无毛或疏被倒刚毛，间或夹杂短腺毛，毛脱落后留有小瘤状突起，老枝灰褐色。冬芽有1对顶端尖的外鳞片，将内鳞片盖没。叶厚纸质或带革质，形态变异很大，从倒卵状椭圆形、椭圆形、圆卵形、卵形至卵状矩圆形，长3～7 (～8.5) 厘米，顶端短尖或具凸尖，基部圆形或阔楔形，两面无毛或仅下面中脉有少数刚伏毛，更或仅下面基部中脉两侧有稍弯短糙毛，有时上面中脉有伏毛，边缘多少有硬睫毛或几无毛；叶柄长2-5毫米，有刚毛。花先于叶或与叶同时开放，芳香，生于幼枝基部苞腋，总花梗长 (2-) 5-10毫米；苞片披针形至近条形，长约为萼筒的2-4倍；相邻两萼筒约连合至中部，长1.5-3毫米，萼檐近截形或微5裂；花冠白色或淡红色，长1-1.5厘米，外面无毛或稀有疏糙毛，唇形，筒长4-5毫米，内面密生柔毛，基部有浅囊，上唇长7-8毫米，裂片深达中部，下唇舌状，长8-10毫米，反曲；雄蕊内藏，花丝长短不一；花柱无毛。果实鲜红色，矩圆形，长约1厘米，部分连合；种子褐色，稍扁，矩圆形，长约3.5毫米，有细凹点。', '花期2月中旬-4月，果熟期4月下旬-5月。', 1527352991, 1527352991, 0);
INSERT INTO `plant` VALUES (48, '草莓', '凤梨、草莓', 'Fragaria × ananassa', 'https://api.aiplants.cn/resource/1/%E8%8D%89%E8%8E%93/129169.jpg#https://api.aiplants.cn/resource/1/%E8%8D%89%E8%8E%93/1387962.jpg#https://api.aiplants.cn/resource/1/%E8%8D%89%E8%8E%93/1645839.jpg#https://api.aiplants.cn/resource/1/%E8%8D%89%E8%8E%93/1671625.jpg', '蔷薇科', '草莓属', '', '', '草莓营养价值丰富，被誉为是“水果皇后”，尤其是所含的维生素C，其含量比苹果、葡萄都高7-10倍。', '原产南美，中国各地及欧洲等地广为栽培。', '　草莓喜温凉气候，草莓根系适温15-22℃，茎叶生长适温为20-30℃。喜光植物，但又有较强的耐荫性。草莓宜生长于肥沃、疏松中性或微酸性壤土中，过于粘重土壤不宜栽培，沙土多施厩肥，勤灌水，也可种草莓。\n', ' 多年生草本植物。茎低于叶或近相等，密被开展黄色柔毛。叶三出，小叶具短柄，质地较厚，倒卵形或菱形，上面深绿色，几无毛，下面淡白绿色，疏生毛，沿脉较密；叶柄密被开展黄色柔毛。聚伞花序，花序下面具一短柄的小叶；花两性；萼片卵形，比副萼片稍长；花瓣白色，近圆形或倒卵椭圆形。聚合果大，宿存萼片直立，紧贴于果实；瘦果尖卵形，光滑。', '花期4-5月，果期6-7月。', 1527353024, 1527353024, 0);
INSERT INTO `plant` VALUES (49, '柑橘', '道县野桔', 'Citrus reticulata', 'https://api.aiplants.cn/resource/1/%E6%9F%91%E6%A9%98/1577166.jpg#https://api.aiplants.cn/resource/1/%E6%9F%91%E6%A9%98/1588635.jpg#https://api.aiplants.cn/resource/1/%E6%9F%91%E6%A9%98/1892749.jpg#https://api.aiplants.cn/resource/1/%E6%9F%91%E6%A9%98/1940782.jpg', '芸香科', '柑橘属', '芸香科柑橘亚科分布在北纬16°~37°之间。是热带、亚热带常绿果树（除枳以外），用作经济栽培的有3个属：枳属、柑橘属和金柑属。中国和世界其他国家栽培的柑橘主要是柑橘属。', '水路东连楚，人烟北接巴。\n山光围一郡，江月照千家。\n庭树纯栽橘，园畦半种茶。\n梦魂知忆处，无夜不京华。\n—— 《 郡斋平望江山 》年代：唐 作者：岑参 ', '营养\n柑橘类水果所含有的人体保健物质，已分离出30余种，其中主要有：类黄酮、单萜、香豆素、类胡萝卜素、类丙醇、吖啶酮、甘油糖脂质等。\n药用\n果实用于小儿腹泻，夜间不眠，发热。\n生态\n柑橘是常绿小乔木，制造氧气，吸收二氧化碳，极具“碳汇”价值。柑橘四季常青，树姿优美，是一种很好的庭园观赏植物。集赏花、观果、闻香于一体的崇明蜜橘，对提高森林覆盖率、绿地率，改善生态环境均有积极意义。', '产秦岭南坡以南、伏牛山南坡诸水系及大别山区南部，向东南至台湾，南至海南岛，西南至西藏东南部海拔较低地区。广泛栽培，很少半野生。偏北部地区栽种的都属橘类，以红橘和朱橘为主。', '柑橘对土壤的适应范围较广，紫色土、红黄壤、沙滩和海涂，pH值4.5～8均可生长，以pH值5.5～6.5为最适宜。柑橘根系生长要求较高的含氧量，以土壤质地疏松，结构良好，有机质含量2%～3%，排水良好的土壤最适宜。柑橘生长发育要求12.5～37℃的温度。秋季花芽分化要求昼夜温度分别为20℃左右和10℃左右，根系生长的土温与地上部大致相同。', '小乔木。分枝多，枝扩展或略下垂，刺较少。单身复叶，翼叶通常狭窄，或仅有痕迹，叶片披针形，椭圆形或阔卵形，大小变异较大，顶端常有凹口，中脉由基部至凹口附近成叉状分枝，叶缘至少上半段通常有钝或圆裂齿，很少全缘。花单生或2-3朵簇生；花萼不规则5-3浅裂；花瓣通常长1.5厘米以内；雄蕊20-25枚，花柱细长，柱头头状。果形种种，通常扁圆形至近圆球形，果皮甚薄而光滑，或厚而粗糙，淡黄色，朱红色或深红色，甚易或稍易剥离，橘络甚多或较少，呈网状，易分离，通常柔嫩，中心柱大而常空，稀充实，瓢囊7-14瓣，稀较多，囊壁薄或略厚，柔嫩或颇韧，汁胞通常纺锤形，短而膨大，稀细长，果肉酸或甜，或有苦味，或另有特异气味；种子或多或少数，稀无籽，通常卵形，顶部狭尖，基部浑圆，子叶深绿、淡绿或间有近于乳白色，合点紫色，多胚，少有单胚。', '花期4-5月，果期10-12月。', 1527422934, 1527422966, 0);
INSERT INTO `plant` VALUES (50, '番荔枝', '洋波罗', 'Annona squamosa', 'https://api.aiplants.cn/resource/1/%E7%95%AA%E8%8D%94%E6%9E%9D/410945.jpg#https://api.aiplants.cn/resource/1/%E7%95%AA%E8%8D%94%E6%9E%9D/410948.jpg#https://api.aiplants.cn/resource/1/%E7%95%AA%E8%8D%94%E6%9E%9D/496518.jpg#https://api.aiplants.cn/resource/1/%E7%95%AA%E8%8D%94%E6%9E%9D/809250.jpg', '番荔枝科', '番荔枝属', '果食用，外形酷似荔枝，故名“番荔枝”，为热带地区著名水果。', '', '树皮纤维可造纸。根可药用，治急性赤痢、精神抑郁、脊髓骨病；果实可治恶疮肿痛，补脾。', '中国浙江、台湾、福建、广东、广西、海南和云南等省区均有栽培。', '喜光耐阴，光照充足植株生长健壮，叶片肥厚。', '落叶小乔木，高3-5米；树皮薄，灰白色，多分枝。叶薄纸质，排成两列，椭圆状披针形，或长圆形，长6-17.5厘米，宽2-7.5厘米，顶端急尖或钝，基部阔楔形或圆形，叶背苍白绿色，初时被微毛，后变无毛；侧脉每边8-15条，上面扁平，下面凸起。花单生或2-4朵聚生于枝顶或与叶对生，长约2厘米，青黄色，下垂；花蕾披针形；萼片三角形，被微毛；外轮花瓣狭而厚，肉质，长圆形，顶端急尖，被微毛，镊合状排列，内轮花瓣极小，退化成鳞片状，被微毛；雄蕊长圆形，药隔宽，顶端近截形；心皮长圆形，无毛，柱头卵状披针形，每心皮有胚珠1颗。果实由多数圆形或椭圆形的成熟心皮微相连易于分开而成的聚合浆果圆球状或心状圆锥形，直径5-10厘米，无毛，黄绿色，外面被白色粉霜。', '花期5-6月，果期6-11月。', 1527422937, 1527422967, 0);
COMMIT;

-- ----------------------------
-- Table structure for question_game
-- ----------------------------
DROP TABLE IF EXISTS `question_game`;
CREATE TABLE `question_game` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `question` text NOT NULL COMMENT '问题',
  `question_image` text NOT NULL COMMENT '问题图片',
  `option_a` text NOT NULL COMMENT '选项A',
  `option_a_image` text NOT NULL COMMENT '选项A图片',
  `option_b` text NOT NULL COMMENT '选项B',
  `option_b_image` text NOT NULL COMMENT '选项B图片',
  `option_c` text COMMENT '选项C',
  `option_c_image` text COMMENT '选项C图片',
  `option_d` text COMMENT '选项D',
  `option_d_image` text COMMENT '选项D图片',
  `answer` int(1) DEFAULT NULL COMMENT '答案 0->A 1->B 2->C 3->D',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '修改时间',
  `delete_time` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for request_time
-- ----------------------------
DROP TABLE IF EXISTS `request_time`;
CREATE TABLE `request_time` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `request_type` int(11) DEFAULT '0' COMMENT '0->植物 1->病虫害',
  `plant_id` int(11) DEFAULT '0' COMMENT '植物id',
  `disease_id` int(11) DEFAULT '0' COMMENT '病虫害id',
  `request_time` int(11) DEFAULT '1' COMMENT '请求次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for search
-- ----------------------------
DROP TABLE IF EXISTS `search`;
CREATE TABLE `search` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `search_type` int(11) DEFAULT '0' COMMENT '0->查询植物 1->查询病虫害',
  `search_name` text NOT NULL COMMENT '查询内容',
  `search_time` int(11) DEFAULT '1' COMMENT '查询次数',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for tour
-- ----------------------------
DROP TABLE IF EXISTS `tour`;
CREATE TABLE `tour` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `name` text NOT NULL COMMENT '名称',
  `area_name` text COMMENT '地区',
  `introduce` text COMMENT '介绍',
  `telephone` text COMMENT '电话',
  `price_desc` text COMMENT '价格',
  `suitable` text COMMENT '旅游时节',
  `big_img` text COMMENT '长图，多个图片路径以#隔开',
  `pic_img` text COMMENT '方图',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '修改时间',
  `delete_time` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for user
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `user_name` varchar(100) NOT NULL COMMENT '用户名 不重复',
  `user_nickname` varchar(100) DEFAULT NULL COMMENT '用户昵称',
  `user_role` int(11) DEFAULT '99' COMMENT '用户角色 0->超级管理员 1->人流量监测管理员 2->后台数据管理员 99->普通用户',
  `user_password` varchar(30) NOT NULL COMMENT '用户密码',
  `user_mobile` varchar(20) DEFAULT NULL COMMENT '联系方式',
  `user_email` varchar(100) DEFAULT NULL COMMENT '电子邮箱',
  `user_avatar` text COMMENT '用户头像',
  `state` int(11) DEFAULT '-1' COMMENT '用户状态 用户封号',
  `create_time` int(11) DEFAULT '0' COMMENT '创建时间',
  `update_time` int(11) DEFAULT '0' COMMENT '修改时间',
  `delete_time` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

SET FOREIGN_KEY_CHECKS = 1;
